const upgradeCost = inputs.upgradeCost || 0;
const annualSavingsPercentage = inputs.annualSavingsPercentage || 0;
const currentAnnualEnergyBill = inputs.currentAnnualEnergyBill || 0;
const inflationRate = inputs.inflationRate || 0;
const upgradeLifespan = inputs.upgradeLifespan || 0;
const rebateAmount = inputs.rebateAmount || 0;
const annualMaintenanceCost = inputs.annualMaintenanceCost || 0;

// 1. Calculate Net Initial Investment
const netInitialCost = Math.max(0, upgradeCost - rebateAmount);

// 2. Calculate Initial Annual Energy Savings (Dollars)
const initialAnnualSavingsDollars = currentAnnualEnergyBill * (annualSavingsPercentage / 100);

// 3. Calculate Net Annual Savings Year 1 (after maintenance)
const netAnnualSavingsYear1 = initialAnnualSavingsDollars - annualMaintenanceCost;

// Initialize outputs
let simplePaybackPeriod = null; // Use null to denote 'N/A' or 'Never'
let totalSavingsLifespan = 0;
let roiPercentage = -100;

// Calculate Simple Payback Period
if (netInitialCost > 0 && netAnnualSavingsYear1 > 0) {
    simplePaybackPeriod = netInitialCost / netAnnualSavingsYear1;
    // If payback period exceeds a reasonable threshold (e.g., 2x lifespan or 50 years), consider it 'null'
    if (simplePaybackPeriod > upgradeLifespan * 2 || simplePaybackPeriod > 50) {
        simplePaybackPeriod = null;
    }
} else if (netInitialCost === 0 && netAnnualSavingsYear1 > 0) {
    simplePaybackPeriod = 0; // Immediate payback if no cost and positive savings
}

// Calculate Total Savings Over Lifespan and ROI
let accumulatedSavingsOverLifespan = 0;
let currentYearEnergyBillProjection = currentAnnualEnergyBill;

for (let year = 1; year <= upgradeLifespan; year++) {
    const currentYearEnergySavings = (currentYearEnergyBillProjection * (annualSavingsPercentage / 100)) - annualMaintenanceCost;
    accumulatedSavingsOverLifespan += currentYearEnergySavings; // Accumulate savings for each year
    currentYearEnergyBillProjection *= (1 + (inflationRate / 100)); // Inflate energy bill for next year's calculation
}

totalSavingsLifespan = Math.max(0, accumulatedSavingsOverLifespan); // Total savings cannot be negative for display

if (netInitialCost > 0) {
    roiPercentage = ((totalSavingsLifespan - netInitialCost) / netInitialCost) * 100;
} else if (netInitialCost === 0) {
    // If no initial cost and there are savings, ROI is infinite.
    // If no initial cost and no savings, ROI is 0.
    roiPercentage = totalSavingsLifespan > 0 ? Infinity : 0;
}

return {
    annualDollarSavings: Math.max(0, netAnnualSavingsYear1), // Ensure display isn't negative for Year 1 savings
    simplePaybackPeriod: simplePaybackPeriod, 
    totalSavingsLifespan: totalSavingsLifespan,
    roiPercentage: roiPercentage
};
