let riskScore = 0;
let floodRiskLevel = "Low";
let estimatedMitigationCost = 0;
let potentialDamageCost = 0;

// 1. Flood Zone Factor
let floodZoneFactor = 0;
const normalizedFloodZone = inputs.flood_zone.toUpperCase().trim();
if (['VE', 'V', 'VO'].includes(normalizedFloodZone)) { 
    floodZoneFactor = 4; // Very high risk zones
} else if (['AE', 'A', 'AO', 'AH', 'AR', 'A1-A30'].includes(normalizedFloodZone)) { 
    floodZoneFactor = 3; // High risk zones
} else if (['X', 'C', 'B'].includes(normalizedFloodZone)) { 
    floodZoneFactor = 1; // Moderate to Low risk zones
} else { 
    floodZoneFactor = 1.5; // Unknown or Preferred, treat as moderate
}
riskScore += floodZoneFactor * 10; // Base risk contribution

// 2. Elevation Factor (lower elevation = higher risk)
// Assuming sea level is 0. Higher elevation is better. Risk increases below 100ft.
let elevationRisk = Math.max(0, 100 - inputs.elevation_above_sea_level);
riskScore += elevationRisk * 0.2; // Each foot below 100ft adds 0.2 to risk score

// 3. Distance to Water Factor (closer = higher risk)
let distanceRisk = 0;
if (inputs.distance_to_water < 0.2) { 
    distanceRisk = 50; // Very close (less than ~1000 ft)
} else if (inputs.distance_to_water < 1) { 
    distanceRisk = 25; // Within 1 mile
} else if (inputs.distance_to_water < 5) { 
    distanceRisk = 10; // Within 5 miles
} else {
    distanceRisk = 0; // Further than 5 miles, minimal direct risk from water body
}
riskScore += distanceRisk;

// 4. Historical Flood Events Factor
riskScore += inputs.historical_flood_events * 15; // Each event adds 15 points to risk

// 5. Basement Presence Factor
if (inputs.basement_presence.toLowerCase() === 'yes') {
    riskScore += 20; // Basements are highly vulnerable to flooding
}

// Cap riskScore to a reasonable max for consistency
riskScore = Math.min(riskScore, 300);

// Determine Flood Risk Level based on riskScore thresholds
if (riskScore < 50) {
    floodRiskLevel = "Low";
} else if (riskScore < 100) {
    floodRiskLevel = "Moderate";
} else if (riskScore < 200) {
    floodRiskLevel = "High";
} else {
    floodRiskLevel = "Very High";
}

// Estimate Mitigation Costs
let baseMitigationPerSqFt = 0;
let riskMultiplierMitigation = 1;
let propertyValueFactorMitigation = 0; 

if (floodRiskLevel === "Low") {
    riskMultiplierMitigation = 1;
    baseMitigationPerSqFt = 3; // Basic sealing, improved gutters, minor landscaping
    propertyValueFactorMitigation = 0.005; // 0.5% of property value
} else if (floodRiskLevel === "Moderate") {
    riskMultiplierMitigation = 1.5;
    baseMitigationPerSqFt = 8; // Sump pump, backflow valves, minor regrading
    propertyValueFactorMitigation = 0.01; // 1% of property value
} else if (floodRiskLevel === "High") {
    riskMultiplierMitigation = 2.5;
    baseMitigationPerSqFt = 20; // Foundation sealing, flood vents, elevation assessment
    propertyValueFactorMitigation = 0.03; // 3% of property value
} else if (floodRiskLevel === "Very High") {
    riskMultiplierMitigation = 4;
    baseMitigationPerSqFt = 50; // Major elevation, full wet/dry floodproofing, major structural changes
    propertyValueFactorMitigation = 0.08; // 8% of property value
}

// Add extra cost for basement floodproofing if applicable
if (inputs.basement_presence.toLowerCase() === 'yes') {
    baseMitigationPerSqFt += 10 * riskMultiplierMitigation; // Additional cost for basement-specific work
}

estimatedMitigationCost = (inputs.square_footage * baseMitigationPerSqFt) + (inputs.property_value * propertyValueFactorMitigation);

// Cap mitigation cost to a reasonable percentage of property value, e.g., 20%
estimatedMitigationCost = Math.min(estimatedMitigationCost, inputs.property_value * 0.20);
// Ensure a minimum mitigation cost for any effort
estimatedMitigationCost = Math.max(estimatedMitigationCost, 1000);

// Estimate Potential Damage Cost (if not mitigated)
let damagePercentage = 0;
if (floodRiskLevel === "Low") {
    damagePercentage = 0.01; // 1% of property value
} else if (floodRiskLevel === "Moderate") {
    damagePercentage = 0.05; // 5% of property value
} else if (floodRiskLevel === "High") {
    damagePercentage = 0.15; // 15% of property value
} else if (floodRiskLevel === "Very High") {
    damagePercentage = 0.30; // 30% of property value, indicating catastrophic potential
}

potentialDamageCost = inputs.property_value * damagePercentage;

// If basement, increase potential damage further
if (inputs.basement_presence.toLowerCase() === 'yes') {
    potentialDamageCost *= 1.2; // Basements increase damage potential by 20%
}

// Cap potential damage cost to the total property value
potentialDamageCost = Math.min(potentialDamageCost, inputs.property_value);

return {
    flood_risk_level: floodRiskLevel,
    estimated_mitigation_cost: estimatedMitigationCost,
    potential_damage_cost: potentialDamageCost
};
