const KWH_PER_BTU = 1 / 3412.14;
const HEATING_HOURS_PER_DAY = 24;
const DEFAULT_HEATING_DAYS_PER_YEAR = 180; // Approximately 6 months of heating season

const FUEL_ENERGY_DENSITIES_KWH_PER_UNIT = {
    "natural gas": 29.307, // kWh per therm (1 therm = 100,000 BTU)
    "electricity": 1,      // kWh per kWh
    "propane": 27.0,       // kWh per gallon (approx. 91,500 BTU/gallon)
    "heating oil": 39.6    // kWh per gallon (approx. 135,000 BTU/gallon)
};

// Define baseline heat loss coefficient factors (simplified for this tool)
// These factors are abstracted to represent relative heat loss impact.
// Higher values indicate more heat loss.
const BASE_HEAT_LOSS_FACTOR_PER_SQFT = 0.005; 
const INSULATION_LOSS_FACTOR_PER_LEVEL = 0.0008; 
const WINDOW_LOSS_FACTOR_PER_LEVEL = 0.0006;   

// Helper function to calculate cost given specific parameters
function calculateCost(livingArea, avgWinterTemp, thermostatSetting, insulationQuality, windowQuality, heatingFuelType, fuelCostPerUnit, furnaceEfficiency, heatingDaysPerYear) {
    // Input validation and clamping
    livingArea = Math.max(1, livingArea || 1);
    avgWinterTemp = avgWinterTemp || 30; // Default for calculations if not provided
    thermostatSetting = Math.max(50, thermostatSetting || 70); // Min 50F
    insulationQuality = Math.min(5, Math.max(1, insulationQuality || 3));
    windowQuality = Math.min(5, Math.max(1, windowQuality || 2));
    fuelCostPerUnit = Math.max(0, fuelCostPerUnit || 0.1);
    furnaceEfficiency = Math.max(10, Math.min(1000, furnaceEfficiency || 80)); // Allow up to 1000% for COP (e.g. 300% for COP 3.0)
    heatingDaysPerYear = Math.max(0, heatingDaysPerYear || DEFAULT_HEATING_DAYS_PER_YEAR);

    const tempDiff = Math.max(0, thermostatSetting - avgWinterTemp);
    if (tempDiff === 0 || heatingDaysPerYear === 0) {
        return 0; // No heating needed or no heating days
    }

    // Calculate effective heat loss coefficient (simplified abstraction)
    // Higher quality (5) means lower loss factor.
    const effectiveInsulationLoss = (6 - insulationQuality) * INSULATION_LOSS_FACTOR_PER_LEVEL;
    const effectiveWindowLoss = (6 - windowQuality) * WINDOW_LOSS_FACTOR_PER_LEVEL;

    const totalHeatLossCoefficient_simplified = BASE_HEAT_LOSS_FACTOR_PER_SQFT + effectiveInsulationLoss + effectiveWindowLoss;

    // Total heat loss rate (e.g., in BTU/hr/degree F) for the whole house
    // Multiplied by an arbitrary factor (100) to scale the simplified coefficient to a more realistic BTU/hr/degF range
    const totalHeatLossRate_BTU_per_hr_per_degF = totalHeatLossCoefficient_simplified * livingArea * 100;

    // Calculate total heat demand over the heating season in BTU
    const annualHeatDemand_BTU = totalHeatLossRate_BTU_per_hr_per_degF * tempDiff * heatingDaysPerYear * HEATING_HOURS_PER_DAY;

    // Convert total heat demand from BTU to kWh
    const annualHeatDemand_kWh = annualHeatDemand_BTU * KWH_PER_BTU;

    // Adjust for heating system efficiency
    const effectiveFurnaceEfficiency = Math.max(10, Math.min(1000, furnaceEfficiency)) / 100; // Cap between 10% and 1000%
    const energyRequired_kWh = annualHeatDemand_kWh / effectiveFurnaceEfficiency;

    // Determine fuel conversion factor
    const fuelTypeLower = (heatingFuelType || "natural gas").toLowerCase();
    const fuelKwhPerUnit = FUEL_ENERGY_DENSITIES_KWH_PER_UNIT[fuelTypeLower] || FUEL_ENERGY_DENSITIES_KWH_PER_UNIT["natural gas"]; // Default to natural gas if unknown

    if (fuelKwhPerUnit <= 0) {
        return 0; // Avoid division by zero or invalid conversion factor
    }

    // Calculate units of fuel consumed and total annual cost
    const unitsConsumed = energyRequired_kWh / fuelKwhPerUnit;
    const annualCost = unitsConsumed * fuelCostPerUnit;

    return annualCost;
}

// Extract and validate inputs
const livingArea = inputs.livingArea;
const avgWinterTemp = inputs.avgWinterTemp;
const thermostatSetting = inputs.thermostatSetting;
const insulationQuality = inputs.insulationQuality;
const windowQuality = inputs.windowQuality;
const heatingFuelType = inputs.heatingFuelType;
const fuelCostPerUnit = inputs.fuelCostPerUnit;
const furnaceEfficiency = inputs.furnaceEfficiency;
const heatingDaysPerYear = DEFAULT_HEATING_DAYS_PER_YEAR; // Using default for now, could be an input later

// Perform base cost calculation
const baseAnnualCost = calculateCost(livingArea, avgWinterTemp, thermostatSetting, insulationQuality, windowQuality, heatingFuelType, fuelCostPerUnit, furnaceEfficiency, heatingDaysPerYear);

// --- Calculate potential savings --- 

// 1. Insulation Upgrade: Improve insulation by 1 level (max 5)
const improvedInsulationQuality = Math.min(5, insulationQuality + 1);
const costWithImprovedInsulation = calculateCost(livingArea, avgWinterTemp, thermostatSetting, improvedInsulationQuality, windowQuality, heatingFuelType, fuelCostPerUnit, furnaceEfficiency, heatingDaysPerYear);
const potentialSavingsInsulation = Math.max(0, baseAnnualCost - costWithImprovedInsulation);

// 2. Window Upgrade: Improve window quality by 1 level (max 5)
const improvedWindowQuality = Math.min(5, windowQuality + 1);
const costWithImprovedWindows = calculateCost(livingArea, avgWinterTemp, thermostatSetting, insulationQuality, improvedWindowQuality, heatingFuelType, fuelCostPerUnit, furnaceEfficiency, heatingDaysPerYear);
const potentialSavingsWindows = Math.max(0, baseAnnualCost - costWithImprovedWindows);

// 3. Thermostat Adjustment: Lower thermostat by 2 degrees (min 65°F)
const improvedThermostatSetting = Math.max(65, thermostatSetting - 2); // Assuming Fahrenheit
const costWithImprovedThermostat = calculateCost(livingArea, avgWinterTemp, improvedThermostatSetting, insulationQuality, windowQuality, heatingFuelType, fuelCostPerUnit, furnaceEfficiency, heatingDaysPerYear);
const potentialSavingsThermostat = Math.max(0, baseAnnualCost - costWithImprovedThermostat);

// Total potential savings (sum of individual improvements)
// Note: These savings are estimated individually and are not strictly additive if all implemented simultaneously. 
// They represent the potential impact of each strategy in isolation for prioritization.
const totalPotentialSavings = potentialSavingsInsulation + potentialSavingsWindows + potentialSavingsThermostat;

return {
    estimatedAnnualCost: baseAnnualCost,
    potentialSavingsInsulation: potentialSavingsInsulation,
    potentialSavingsWindows: potentialSavingsWindows,
    potentialSavingsThermostat: potentialSavingsThermostat,
    totalPotentialSavings: totalPotentialSavings
};
