function calculateRValue(inputs) {
    const { heatingDegreeDays, coolingDegreeDays, insulationLocation, targetEfficiencyLevel, initialRValue } = inputs;

    let baseRValue;
    let maxRValue;

    switch (insulationLocation.toLowerCase().trim()) {
        case 'attic/roof':
        case 'attic':
        case 'roof':
            baseRValue = 30; // Base for moderate climate, standard efficiency
            maxRValue = 60;
            break;
        case 'walls':
            baseRValue = 15;
            maxRValue = 30;
            break;
        case 'floor':
            baseRValue = 10;
            maxRValue = 25;
            break;
        default:
            // Return default or error for invalid location
            return {
                recommendedRValueTotal: 0,
                additionalRValueNeeded: 0,
                estimatedAnnualSavingsPercentage: 0
            };
    }

    // Climate Severity Factor
    // This factor adjusts the base R-value based on the severity of heating and cooling needs.
    let climateSeverityFactor = 1.0;
    if (heatingDegreeDays > 6000 && coolingDegreeDays < 1000) { // Very cold dominant
        climateSeverityFactor = 1.4;
    } else if (heatingDegreeDays > 4000 && coolingDegreeDays < 1500) { // Cold dominant
        climateSeverityFactor = 1.25;
    } else if (coolingDegreeDays > 2500 && heatingDegreeDays < 2000) { // Very hot dominant
        climateSeverityFactor = 1.3;
    } else if (coolingDegreeDays > 1500 && heatingDegreeDays < 4000) { // Hot dominant
        climateSeverityFactor = 1.15;
    } else if (heatingDegreeDays < 2000 && coolingDegreeDays < 1000) { // Mild all year
        climateSeverityFactor = 0.8;
    } else if (heatingDegreeDays > 4000 && coolingDegreeDays > 1500) { // Extreme all year (hot summers, cold winters)
        climateSeverityFactor = 1.35;
    } else { // Moderate or balanced
        climateSeverityFactor = 1.0;
    }

    // Efficiency Level Multiplier
    let efficiencyMultiplier;
    switch (targetEfficiencyLevel.toLowerCase().trim()) {
        case 'standard': efficiencyMultiplier = 1.0; break;
        case 'good': efficiencyMultiplier = 1.2; break;
        case 'excellent': efficiencyMultiplier = 1.5; break;
        case 'max savings': efficiencyMultiplier = 1.8; break;
        default: efficiencyMultiplier = 1.0; // Default to standard if unrecognized
    }

    // Calculate the raw recommended R-value
    let calculatedRValue = baseRValue * climateSeverityFactor * efficiencyMultiplier;

    // Round to the nearest 5 R-value increment and cap at the maximum practical R-value for the location
    const recommendedRValueTotal = Math.min(Math.round(calculatedRValue / 5) * 5, maxRValue);

    // Calculate additional R-value needed, ensuring it's not negative
    const additionalRValueNeeded = Math.max(0, recommendedRValueTotal - initialRValue);

    // Estimated Annual Savings Percentage
    // U-value is the inverse of R-value (U = 1/R). Energy savings are proportional to the reduction in U-value.
    // We assume a baseline R-value of 3 for an uninsulated component to prevent division by zero and provide a realistic starting point.
    const effectiveInitialRValue = initialRValue > 0 ? initialRValue : 3; 

    const u_old = 1 / effectiveInitialRValue;
    const u_new = 1 / recommendedRValueTotal;

    let estimatedAnnualSavingsPercentage = 0;
    if (recommendedRValueTotal > effectiveInitialRValue && recommendedRValueTotal > 0) {
        estimatedAnnualSavingsPercentage = (1 - (u_new / u_old)) * 100;
        // Cap savings at a realistic maximum for insulation improvements, typically 50-60% for significant upgrades.
        estimatedAnnualSavingsPercentage = Math.min(estimatedAnnualSavingsPercentage, 60);
        // Ensure a minimum reasonable savings if significant additional insulation is recommended.
        if (additionalRValueNeeded > 5 && estimatedAnnualSavingsPercentage < 10) {
            estimatedAnnualSavingsPercentage = 10;
        }
    }

    // Round the savings percentage to one decimal place and ensure it's not negative
    estimatedAnnualSavingsPercentage = Math.max(0, Math.round(estimatedAnnualSavingsPercentage * 10) / 10);

    return {
        recommendedRValueTotal: recommendedRValueTotal,
        additionalRValueNeeded: additionalRValueNeeded,
        estimatedAnnualSavingsPercentage: estimatedAnnualSavingsPercentage
    };
}
