const initialAnnualEnergyCost = inputs.initialAnnualEnergyCost || 0;
const insulationUpgradeCost = inputs.insulationUpgradeCost || 0;
const estimatedEnergySavingsPercentage = inputs.estimatedEnergySavingsPercentage || 0;
const annualEnergyCostInflationRate = inputs.annualEnergyCostInflationRate || 0;
const investmentHorizonYears = inputs.investmentHorizonYears || 1;
const rebateOrTaxCredit = inputs.rebateOrTaxCredit || 0;

// Cap energy savings percentage at 100% and ensure it's non-negative
const actualSavingsPercentage = Math.max(0, Math.min(100, estimatedEnergySavingsPercentage));

// 1. Calculate Annual Energy Savings in dollars
const annualEnergySavingsDollars = initialAnnualEnergyCost * (actualSavingsPercentage / 100);

// 2. Calculate Net Investment Cost
const netInvestment = Math.max(0, insulationUpgradeCost - rebateOrTaxCredit);

// 3. Calculate Simple Payback Period
let paybackPeriodYears = Infinity;
if (annualEnergySavingsDollars > 0) {
    paybackPeriodYears = netInvestment / annualEnergySavingsDollars;
}
// Ensure payback period is not negative if net investment is zero or negative with savings
paybackPeriodYears = (netInvestment <= 0 && annualEnergySavingsDollars > 0) ? 0 : paybackPeriodYears; // Immediate payback if net cost is 0 or negative and there are savings
paybackPeriodYears = (annualEnergySavingsDollars <= 0 && netInvestment > 0) ? Infinity : paybackPeriodYears; // No payback if no savings and positive investment

// 4. Calculate Total Savings Over Investment Horizon, accounting for inflation
let totalSavingsOverHorizon = 0;
let currentYearSavings = annualEnergySavingsDollars;
const inflationFactor = 1 + (annualEnergyCostInflationRate / 100);

for (let i = 0; i < investmentHorizonYears; i++) {
    totalSavingsOverHorizon += currentYearSavings;
    currentYearSavings *= inflationFactor;
}

// 5. Calculate Return on Investment (ROI)
let roiPercentage = -100; // Default to -100% if net investment is positive but no savings
if (netInvestment > 0) {
    roiPercentage = ((totalSavingsOverHorizon - netInvestment) / netInvestment) * 100;
} else if (netInvestment === 0 && totalSavingsOverHorizon > 0) {
    roiPercentage = Infinity; // Infinite ROI if investment is zero but there are savings
} else if (netInvestment === 0 && totalSavingsOverHorizon === 0) {
    roiPercentage = 0; // No investment, no savings, so 0 ROI
} else if (netInvestment < 0) { // If net investment is negative (rebates exceed cost)
    roiPercentage = (totalSavingsOverHorizon - netInvestment) / Math.abs(netInvestment) * 100; // Treat as infinite if negative net investment and positive savings
    if (totalSavingsOverHorizon > 0) roiPercentage = Infinity; else roiPercentage = 0; // Simplistic for negative investment
}

return {
    annualEnergySavingsDollars: annualEnergySavingsDollars,
    netInvestment: netInvestment,
    paybackPeriodYears: paybackPeriodYears,
    totalSavingsOverHorizon: totalSavingsOverHorizon,
    roiPercentage: roiPercentage
};
