const { upfrontCost, monthlyMonitoringFee, systemLifespanYears, annualHomeInsurancePremium, insuranceDiscountPercentage, averagePropertyLossNoSecurity, riskReductionPercentage, annualMaintenanceCost, propertyValueIncreaseFactorPercentage, currentPropertyValue } = inputs;

// Ensure non-negative and valid numbers for calculation, capping percentages at 100.
const L = Math.max(1, systemLifespanYears);
const safeUpfrontCost = Math.max(0, upfrontCost);
const safeMonthlyMonitoringFee = Math.max(0, monthlyMonitoringFee);
const safeAnnualMaintenanceCost = Math.max(0, annualMaintenanceCost);
const safeAnnualHomeInsurancePremium = Math.max(0, annualHomeInsurancePremium);
const safeInsuranceDiscountPercentage = Math.min(Math.max(0, insuranceDiscountPercentage), 100);
const safeAveragePropertyLossNoSecurity = Math.max(0, averagePropertyLossNoSecurity);
const safeRiskReductionPercentage = Math.min(Math.max(0, riskReductionPercentage), 100);
const safePropertyValueIncreaseFactorPercentage = Math.min(Math.max(0, propertyValueIncreaseFactorPercentage), 100);
const safeCurrentPropertyValue = Math.max(0, currentPropertyValue);

// --- Costs Calculation ---
const totalUpfrontCost = safeUpfrontCost;
const totalRecurringCostOverLifespan = (safeMonthlyMonitoringFee * 12 + safeAnnualMaintenanceCost) * L;
const totalCostOfOwnership = totalUpfrontCost + totalRecurringCostOverLifespan;

// --- Benefits Calculation ---
// 1. Insurance Savings
const annualInsuranceSavings = safeAnnualHomeInsurancePremium * (safeInsuranceDiscountPercentage / 100);
const totalInsuranceSavingsOverLifespan = annualInsuranceSavings * L;

// 2. Avoided Loss Benefit (due to reduced risk of incidents)
const avoidedAnnualLoss = safeAveragePropertyLossNoSecurity * (safeRiskReductionPercentage / 100);
const totalAvoidedLossBenefitOverLifespan = avoidedAnnualLoss * L;

// 3. Property Value Increase Benefit
const totalPropertyValueIncreaseBenefit = safeCurrentPropertyValue * (safePropertyValueIncreaseFactorPercentage / 100);

// --- Summing All Benefits ---
const totalBenefits = totalInsuranceSavingsOverLifespan + totalAvoidedLossBenefitOverLifespan + totalPropertyValueIncreaseBenefit;

// --- Net Financial Benefit & ROI Calculation ---
const netFinancialBenefit = totalBenefits - totalCostOfOwnership;

let roiPercentage = 0;
if (totalCostOfOwnership > 0) {
    roiPercentage = (netFinancialBenefit / totalCostOfOwnership) * 100;
} else if (netFinancialBenefit > 0) {
    // If cost is zero but there are benefits, ROI is effectively infinite.
    // Using a large number for practical display.
    roiPercentage = Number.MAX_SAFE_INTEGER;
} else {
    // If both cost and net benefit are zero, or cost is zero and net benefit is negative,
    // ROI is 0 or undefined. We'll set it to 0 for a neutral state.
    roiPercentage = 0;
}

return {
    totalUpfrontCost: totalUpfrontCost,
    totalRecurringCostOverLifespan: totalRecurringCostOverLifespan,
    totalInsuranceSavingsOverLifespan: totalInsuranceSavingsOverLifespan,
    totalAvoidedLossBenefitOverLifespan: totalAvoidedLossBenefitOverLifespan,
    totalPropertyValueIncreaseBenefit: totalPropertyValueIncreaseBenefit,
    netFinancialBenefit: netFinancialBenefit,
    roiPercentage: roiPercentage
};
