{
  const annual_electricity_kwh = inputs.annual_electricity_kwh;
  const average_electricity_cost_kwh = inputs.average_electricity_cost_kwh;
  const total_system_cost = inputs.total_system_cost;
  const federal_tax_credit_percent = inputs.federal_tax_credit_percent;
  const state_local_rebate = inputs.state_local_rebate;
  const annual_electricity_inflation_percent = inputs.annual_electricity_inflation_percent;
  const system_lifespan_years = inputs.system_lifespan_years;

  // 1. Calculate Net System Cost
  const federalTaxCreditAmount = total_system_cost * (federal_tax_credit_percent / 100);
  const netSystemCost = total_system_cost - federalTaxCreditAmount - state_local_rebate;

  // Cap netSystemCost at 0 for effective calculation, as it cannot be negative for ROI percentage purposes
  const effectiveNetSystemCost = Math.max(0, netSystemCost);

  // 2. Calculate Annual Savings (Year 1)
  // Assuming the solar system offsets 100% of the annual electricity consumption for savings calculation
  const annualSavingsYear1 = annual_electricity_kwh * average_electricity_cost_kwh;

  // 3. Calculate Payback Period
  let paybackYears = 0;
  let remainingCostForPayback = effectiveNetSystemCost;

  if (effectiveNetSystemCost <= 0) {
    paybackYears = 0;
  } else {
    let paidBack = false;
    for (let i = 0; i < system_lifespan_years * 2; i++) { // Cap iterations to prevent infinite loops for non-paying systems
      const currentYearSavings = annualSavingsYear1 * Math.pow(1 + annual_electricity_inflation_percent / 100, i);
      remainingCostForPayback -= currentYearSavings;
      if (remainingCostForPayback <= 0) {
        paybackYears = i + 1;
        paidBack = true;
        break;
      }
    }
    if (!paidBack) {
      paybackYears = Infinity; // Did not pay back within double the system lifespan
    }
  }

  // 4. Calculate Total Savings Over System Lifespan
  let totalSavingsOverLifespan = 0;
  for (let i = 0; i < system_lifespan_years; i++) {
    totalSavingsOverLifespan += annualSavingsYear1 * Math.pow(1 + annual_electricity_inflation_percent / 100, i);
  }

  // 5. Calculate Return on Investment (ROI)
  let roi_percent = 0;
  if (effectiveNetSystemCost > 0) {
    roi_percent = ((totalSavingsOverLifespan - effectiveNetSystemCost) / effectiveNetSystemCost) * 100;
  } else {
    // If effectiveNetSystemCost is 0 or negative (due to very high incentives), ROI is considered exceptionally high.
    roi_percent = 9999;
  }

  return {
    net_cost: netSystemCost,
    annual_savings_year1: annualSavingsYear1,
    payback_period_years: paybackYears,
    total_savings_lifespan: totalSavingsOverLifespan,
    roi_percent: roi_percent
  };
}
