(function(inputs) {
    const {
        system_size_kw,
        total_installed_cost,
        current_electricity_rate,
        annual_electricity_consumption,
        system_performance_ratio,
        electricity_inflation_rate,
        solar_degradation_rate,
        federal_tax_credit_percent,
        state_local_rebates,
        annual_maintenance_cost_percent,
        project_lifespan_years
    } = inputs;

    // --- Input Validation & Edge Cases ---
    if (system_size_kw <= 0 || total_installed_cost <= 0 || current_electricity_rate <= 0 || annual_electricity_consumption <= 0 || project_lifespan_years <= 0) {
        return {
            error: "Please ensure all primary financial and system size inputs are positive values."
        };
    }
    if (system_performance_ratio <= 0 || system_performance_ratio > 100) {
        return {
            error: "System performance ratio must be between 1% and 100%."
        };
    }
    if (solar_degradation_rate < 0 || solar_degradation_rate > 5) {
         return {
            error: "Solar degradation rate must be between 0% and 5% annually for realistic projections."
        };
    }
    if (federal_tax_credit_percent < 0 || federal_tax_credit_percent > 100) {
        return {
            error: "Federal tax credit percentage must be between 0% and 100%."
        };
    }
    if (electricity_inflation_rate < -5 || electricity_inflation_rate > 10) {
         return {
            error: "Electricity inflation rate should be between -5% and 10% annually for realistic projections."
        };
    }
    if (annual_maintenance_cost_percent < 0 || annual_maintenance_cost_percent > 5) {
         return {
            error: "Annual maintenance cost percentage should be between 0% and 5% of system cost for realistic projections."
        };
    }

    // --- Constants & Conversions ---
    const peak_sun_hours_daily_average = 4; // A common average for many US regions, simplified. For a professional tool this might be zip-code dependent.
    const performance_factor = system_performance_ratio / 100;
    const electricity_inflation_factor = 1 + (electricity_inflation_rate / 100);
    const solar_degradation_factor = 1 - (solar_degradation_rate / 100);
    const federal_tax_credit_factor = federal_tax_credit_percent / 100;

    // --- Step 1: Calculate Net System Cost ---
    const federal_tax_credit_amount = total_installed_cost * federal_tax_credit_factor;
    const net_system_cost_after_incentives = total_installed_cost - federal_tax_credit_amount - state_local_rebates;

    // Ensure net cost is not negative due to extremely high incentives
    const net_project_cost = Math.max(0, net_system_cost_after_incentives);

    // --- Initialize cumulative values ---
    let cumulative_electricity_savings = 0;
    let cumulative_maintenance_costs = 0;
    let current_annual_production = system_size_kw * 1000 * peak_sun_hours_daily_average * 365 * performance_factor; // Year 1 production in kWh
    let current_electricity_rate_year_n = current_electricity_rate;
    let payback_period_years = null;
    let year1_net_savings = 0; // Renamed to clarify it's net of year 1 maintenance

    // --- Loop through each year of the project lifespan ---
    for (let year = 1; year <= project_lifespan_years; year++) {
        // Annual electricity production (degrades each year)
        const annual_production_this_year = current_annual_production;

        // Electricity savings for this year (capped by consumption, uses inflated rate)
        // Assumes all produced electricity up to consumption is used/net-metered at current retail rate.
        const effective_energy_saved_kwh = Math.min(annual_production_this_year, annual_electricity_consumption);
        const annual_electricity_savings_this_year = effective_energy_saved_kwh * current_electricity_rate_year_n;

        // Annual maintenance and insurance cost (constant as a percentage of initial total cost)
        const annual_maintenance_cost_this_year = total_installed_cost * (annual_maintenance_cost_percent / 100);

        // Accumulate totals
        cumulative_electricity_savings += annual_electricity_savings_this_year;
        cumulative_maintenance_costs += annual_maintenance_cost_this_year;

        if (year === 1) {
            year1_net_savings = annual_electricity_savings_this_year - annual_maintenance_cost_this_year;
        }

        // Check for payback period
        if (payback_period_years === null && (cumulative_electricity_savings - cumulative_maintenance_costs) >= net_project_cost) {
            payback_period_years = year;
        }

        // Update for next year's iteration
        current_annual_production *= solar_degradation_factor; // Solar output degrades
        current_electricity_rate_year_n *= electricity_inflation_factor; // Electricity price inflates
    }

    // --- Calculate final outputs ---
    const total_net_financial_benefit = cumulative_electricity_savings - cumulative_maintenance_costs - net_project_cost;

    // ROI calculation: (Total Net Benefit / Net Project Cost) * 100%
    let total_roi_percent = 0;
    if (net_project_cost > 0) {
        total_roi_percent = (total_net_financial_benefit / net_project_cost) * 100;
    } else if (total_net_financial_benefit > 0) {
        // If net_project_cost is 0 or negative (due to very high incentives), and there's a benefit, ROI is effectively infinite.
        total_roi_percent = Infinity; 
    }


    // Handle cases where payback period might not be reached
    let final_payback_period_display = payback_period_years;
    if (payback_period_years === null) {
        final_payback_period_display = project_lifespan_years + "+ (Not Reached)"; // Indicate it wasn't reached within the project lifespan
    } else if (payback_period_years === 0) {
        final_payback_period_display = "<1"; // Less than 1 year
    }

    return {
        net_system_cost: net_project_cost,
        total_electricity_savings: cumulative_electricity_savings,
        total_maintenance_costs: cumulative_maintenance_costs,
        total_net_financial_benefit: total_net_financial_benefit,
        payback_period_years: final_payback_period_display,
        total_roi_percent: total_roi_percent,
        year1_savings: year1_net_savings
    };
})
