const { forecastSeverity, numberOfOccupants, hasVulnerableOccupants, primaryHeatingSource, backupPowerSource, currentEmergencyFoodDays, currentEmergencyWaterGallons, homeInsulationRating } = inputs;

const checklist = [];

// --- Configuration Constants ---
const BASE_PREP_DAYS_PER_SEVERITY = [0, 1, 2, 3, 5, 7]; // Days for severity 0 (unused), 1, 2, 3, 4, 5
const WATER_GALLONS_PER_PERSON_PER_DAY = 1; // Minimum for drinking and sanitation
const ADDITIONAL_WATER_PER_SEVERITY_POINT = 0.5; // Additional gallons per person per day per severity point
const FOOD_DAYS_BUFFER_PER_SEVERITY_POINT = 0.5; // Additional days of food per severity point
const MEDICAL_KIT_AMPLIFIER_VULNERABLE = 1.5; // Factor to increase medical items for vulnerable

// --- 1. Determine Preparedness Duration ---
const recommendedPrepDays = BASE_PREP_DAYS_PER_SEVERITY[forecastSeverity] || 1;

// --- 2. Calculate Food & Water Needs ---
let requiredFoodDays = Math.max(3, recommendedPrepDays + (forecastSeverity * FOOD_DAYS_BUFFER_PER_SEVERITY_POINT));
if (hasVulnerableOccupants === 1) requiredFoodDays += 1; // Add an extra day for vulnerable
requiredFoodDays = Math.ceil(Math.min(requiredFoodDays, 14)); // Cap at 14 days

let requiredWaterGallons = Math.max(3, numberOfOccupants * WATER_GALLONS_PER_PERSON_PER_DAY * recommendedPrepDays);
requiredWaterGallons += numberOfOccupants * (forecastSeverity * ADDITIONAL_WATER_PER_SEVERITY_POINT);
if (hasVulnerableOccupants === 1) requiredWaterGallons += numberOfOccupants * 0.5; // Add extra for vulnerable
requiredWaterGallons = Math.ceil(Math.min(requiredWaterGallons, 50)); // Cap at 50 gallons

// --- 3. Generate Checklist Categories and Items ---

// Food & Water
const foodWaterItems = [];
if (currentEmergencyFoodDays < requiredFoodDays) {
  foodWaterItems.push(`**Emergency Food Supply:** Stock at least ${requiredFoodDays} days of non-perishable food per person. You currently have ${currentEmergencyFoodDays} days.`);
}
if (currentEmergencyWaterGallons < requiredWaterGallons) {
  foodWaterItems.push(`**Emergency Water Supply:** Stock at least ${requiredWaterGallons} gallons of water (for drinking and sanitation). You currently have ${currentEmergencyWaterGallons} gallons.`);
}
if (foodWaterItems.length === 0) foodWaterItems.push('Sufficient emergency food and water supplies reported.');
foodWaterItems.push('Manual can opener and utility knife.');
foodWaterItems.push('Disposable plates, cups, and utensils.');
foodWaterItems.push('Pet food and extra water for pets (if applicable).');
checklist.push({ category: 'Food & Water Supplies', items: foodWaterItems });

// Heating & Shelter
const heatingShelterItems = [
  'Blankets, sleeping bags, and extra warm clothing for each person.',
  'Emergency thermal blankets.',
  'Close off unused rooms to conserve heat.',
  'Insulate windows and doors with weather stripping or plastic film.'
];

if (homeInsulationRating < 3) {
  heatingShelterItems.push('**Improve Home Insulation:** Consider sealing drafts, adding attic insulation, or upgrading windows to retain heat better.');
}
if (primaryHeatingSource === 'Electric' || primaryHeatingSource === 'Heat Pump') {
  if (backupPowerSource === 'None' || backupPowerSource === 'Small UPS/Power Banks') {
    heatingShelterItems.push('**Backup Heating Plan:** Develop a plan for alternative safe heating (e.g., propane heater, wood stove) if primary electric heat fails during a power outage. Ensure proper ventilation!');
  }
}
if (forecastSeverity >= 4) {
  heatingShelterItems.push('**Generator Fuel:** Ensure you have ample fuel (gasoline, propane, diesel) for your generator, stored safely.');
  heatingShelterItems.push('**Carbon Monoxide Detector:** With battery backup, especially if using alternative heating or a generator.');
}
if (backupPowerSource === 'Fireplace with wood') {
  heatingShelterItems.push('**Fireplace Preparedness:** Ensure chimney is clean, and have enough seasoned firewood on hand.');
}
checklist.push({ category: 'Heating & Shelter', items: heatingShelterItems });

// Power & Lighting
const powerLightingItems = [
  'Flashlights with extra batteries.',
  'Battery-powered lanterns or headlamps.',
  'Solar or hand-crank charger for mobile devices.',
  'Power banks for charging phones and small devices.'
];

if (backupPowerSource === 'None' || backupPowerSource === 'Small UPS/Power Banks') {
  if (forecastSeverity >= 3) {
    powerLightingItems.push('**Consider Generator/Large Battery Backup:** For extended outages, a generator or large capacity battery backup system is highly recommended.');
  }
}
if (primaryHeatingSource === 'Electric' || primaryHeatingSource === 'Heat Pump' || primaryHeatingSource === 'Other') {
  if (backupPowerSource === 'None' || backupPowerSource === 'Small UPS/Power Banks') {
     powerLightingItems.push('**Power Outage Strategy:** Plan how you will cope without power for extended periods, especially if your heating relies on electricity.');
  }
}
if (forecastSeverity >= 4) {
  powerLightingItems.push('**Fuel Storage Safety:** Review safe storage practices for generator fuel and ensure adequate ventilation if operating a generator.');
}
checklist.push({ category: 'Power & Lighting', items: powerLightingItems });

// Health & Safety
const healthSafetyItems = [
  'First aid kit (well-stocked).',
  'Any prescription medications (at least a 7-day supply).',
  'Over-the-counter medications (pain relievers, allergy meds, antacids).',
  'Eyeglasses/contact lenses and supplies.','Sunscreen and insect repellent (for potential post-storm cleanup/outdoor work).'
];

if (hasVulnerableOccupants === 1) {
  healthSafetyItems.push('**Special Medical Needs:** Extra supplies for infants, elderly, or those with medical conditions (e.g., diapers, formula, oxygen, medical equipment batteries).');
  healthSafetyItems.push('**Medical Information:** Keep copies of medical records, insurance cards, and emergency contact information readily accessible.');
  healthSafetyItems.push('**List of Important Contacts:** Emergency services, doctors, family, neighbors, utility companies.');
}
if (forecastSeverity >= 3) {
  healthSafetyItems.push('**Winter-specific First Aid:** Include items for hypothermia, frostbite, and cold-related injuries.');
}
checklist.push({ category: 'Health & Safety', items: healthSafetyItems });

// Communication & Information
const communicationItems = [
  'Battery-powered or hand-crank NOAA weather radio.',
  'Fully charged mobile phones and chargers.',
  'List of important phone numbers (family, friends, doctors, utility companies) written on paper.',
  'Local maps in case GPS is unavailable.'
];
if (forecastSeverity >= 3) {
  communicationItems.push('**Two-way Radios (Walkie-Talkies):** If cell service is unreliable, for communication within your property or immediate neighborhood.');
  communicationItems.push('**Emergency Alert Subscriptions:** Sign up for local emergency alerts.');
}
checklist.push({ category: 'Communication & Information', items: communicationItems });

// Home Maintenance & Vehicle Prep
const homeVehicleItems = [
  'Shovel (snow shovel, sturdy all-purpose shovel).',
  'Rock salt or ice melt.',
  'Tools for basic repairs (wrench to turn off utilities if needed).',
  'Winterize outdoor faucets and exposed pipes.',
  'Ensure vehicle has a full tank of gas, emergency kit, warm blankets, and jumper cables.'
];

if (forecastSeverity >= 4) {
  homeVehicleItems.push('**Generator Maintenance:** Test your generator regularly and ensure it's in good working order.');
  homeVehicleItems.push('**Tree & Branch Trimming:** Inspect trees near your home and power lines; trim any weak or overhanging branches.');
  homeVehicleItems.push('**Gutter Cleaning:** Ensure gutters are clear to prevent ice dams.');
}
if (primaryHeatingSource === 'Fuel Oil' || primaryHeatingSource === 'Propane') {
  homeVehicleItems.push('**Fuel Tank Level:** Check fuel oil/propane tank levels; schedule a refill if low.');
}
checklist.push({ category: 'Home Maintenance & Vehicle Prep', items: homeVehicleItems });

// General Preparedness
const generalItems = [
  'Cash in small denominations.',
  'Important documents (birth certificates, deeds, insurance policies) in a waterproof container.',
  'Whistle to signal for help.',
  'Dust masks to filter contaminated air.'
];
if (forecastSeverity >= 4) {
  generalItems.push('**Evacuation Plan:** Discuss and practice a family evacuation plan, including designated meeting points.');
  generalItems.push('**Neighbor Check-in Plan:** Establish a plan to check on elderly or vulnerable neighbors.');
}
checklist.push({ category: 'General Preparedness', items: generalItems });

return { preparednessChecklist: checklist };
