const { initial_investment_cost, estimated_annual_energy_savings_percentage, current_annual_energy_bill, expected_lifespan_of_improvement, annual_energy_price_increase_rate, maintenance_cost_annual_percentage, discount_rate } = inputs;

// Input validation and default to 0 for non-positive values where applicable to prevent errors.
const invCost = Math.max(0, initial_investment_cost);
const energySavingsPct = Math.max(0, Math.min(100, estimated_annual_energy_savings_percentage)); // Cap at 100%
const currentBill = Math.max(0, current_annual_energy_bill);
const lifespan = Math.max(1, expected_lifespan_of_improvement); // Minimum 1 year lifespan
const priceIncreaseRate = Math.max(0, annual_energy_price_increase_rate);
const maintCostPct = Math.max(0, Math.min(100, maintenance_cost_annual_percentage)); // Cap at 100%
const discRate = Math.max(0, discount_rate);

// Step 1: Calculate Year 1 Annual Energy Savings
const annualEnergySavingsYear1 = currentBill * (energySavingsPct / 100);

// Step 2: Calculate Year 1 Annual Maintenance Cost
const annualMaintenanceCostYear1 = invCost * (maintCostPct / 100);

// Step 3: Calculate Net Annual Cash Flow for Year 1
const netAnnualCashFlowYear1 = annualEnergySavingsYear1 - annualMaintenanceCostYear1;

// Step 4: Calculate Simple Payback Period
let simplePaybackPeriod = Infinity;
if (netAnnualCashFlowYear1 > 0 && invCost > 0) {
    simplePaybackPeriod = invCost / netAnnualCashFlowYear1;
} else if (invCost === 0 && netAnnualCashFlowYear1 >= 0) {
    simplePaybackPeriod = 0; // Immediate payback if no cost and non-negative cash flow
}

// Step 5: Calculate Total Net Cash Flow Over Lifespan & Net Present Value (NPV)
let totalNetCashFlowOverLifespan = -invCost; // Start with initial investment as negative cash flow
let npv = -invCost; // Start NPV with initial investment

let cumulativeAnnualNetCashFlow = 0; // This variable tracks the sum of annual net cash flows (savings - maintenance)

for (let year = 1; year <= lifespan; year++) {
    const projectedEnergyBill = currentBill * Math.pow(1 + (priceIncreaseRate / 100), year - 1);
    const grossEnergySavingsThisYear = projectedEnergyBill * (energySavingsPct / 100);
    const maintenanceCostThisYear = invCost * (maintCostPct / 100); // Assuming maintenance cost is constant in nominal terms

    const netCashFlowThisYear = grossEnergySavingsThisYear - maintenanceCostThisYear;
    cumulativeAnnualNetCashFlow += netCashFlowThisYear;

    totalNetCashFlowOverLifespan += netCashFlowThisYear; // Add to the running total (which started with -invCost)

    // Calculate NPV for this year's cash flow
    if (discRate >= 0) { // Ensure discount rate is not negative, it would inflate future values
        npv += netCashFlowThisYear / Math.pow(1 + (discRate / 100), year);
    } else { // Handle negative discount rates (though uncommon for ROI, important for robustness)
        npv += netCashFlowThisYear * Math.pow(1 + (-discRate / 100), year); // Treat as negative interest
    }
}

// Step 6: Calculate Annualized ROI
let annualizedRoi = 0;
if (invCost > 0 && lifespan > 0) {
    // This is a simplified annualized ROI, representing the average annual return on investment.
    annualizedRoi = (totalNetCashFlowOverLifespan / invCost) / lifespan * 100;
} else if (invCost === 0) {
    annualizedRoi = (cumulativeAnnualNetCashFlow > 0 ? Infinity : 0); // If no cost, infinite ROI for positive cash flow, else 0
}

return {
    annual_energy_savings_year1: annualEnergySavingsYear1,
    net_annual_cash_flow_year1: netAnnualCashFlowYear1,
    simple_payback_period: simplePaybackPeriod,
    total_net_cash_flow_over_lifespan: totalNetCashFlowOverLifespan,
    net_present_value: npv,
    annualized_roi: annualizedRoi
};
