const totalAvailableFunds = monthlyIncome + snapBenefits;
const totalNonFoodExpenses = housingCost + transportationCost + utilitiesCost + healthcareCost + debtPayments;

// Determine how much SNAP covers the food budget
const snapContributionToFood = Math.min(snapBenefits, targetFoodBudget);
const incomeContributionToFood = Math.max(0, targetFoodBudget - snapContributionToFood); // Income covers the rest of the target food budget

const totalAllocatedExpenses = totalNonFoodExpenses + targetFoodBudget;
const remainingDiscretionaryBudget = totalAvailableFunds - totalAllocatedExpenses;

let snapUtilizationPercentage = 0;
if (snapBenefits > 0) {
    snapUtilizationPercentage = (snapContributionToFood / snapBenefits) * 100;
}

return {
    totalAvailableFunds: totalAvailableFunds,
    totalNonFoodExpenses: totalNonFoodExpenses,
    allocatedFoodBudget: targetFoodBudget,
    incomeContributionToFood: incomeContributionToFood,
    snapContributionToFood: snapContributionToFood,
    remainingDiscretionaryBudget: remainingDiscretionaryBudget,
    snapUtilizationPercentage: snapUtilizationPercentage
};
