const householdMembers = inputs.householdMembers || 1;
const annualElectricityKwh = inputs.annualElectricityKwh || 0;
const electricityReductionPercent = Math.min(Math.max(inputs.electricityReductionPercent || 0, 0), 100);
const annualCarMiles = inputs.annualCarMiles || 0;
const carMpg = Math.max(inputs.carMpg || 1, 1); // Avoid division by zero
const carReductionPercent = Math.min(Math.max(inputs.carReductionPercent || 0, 0), 100);
const dietShiftPercent = Math.min(Math.max(inputs.dietShiftPercent || 0, 0), 100);
const annualWasteKg = inputs.annualWasteKg || 0;

// Emission Factors (average estimates)
const EF_ELECTRICITY = 0.475; // kg CO2e per kWh (US average)
const EF_GASOLINE_GALLON = 8.89; // kg CO2e per gallon
const EF_AVG_DIET_PER_PERSON = 2500; // kg CO2e per person per year (average diet baseline)
const DIET_MAX_REDUCTION_FACTOR = 0.5; // Max 50% reduction for diet emissions with a full plant-based shift
const EF_WASTE_TO_LANDFILL = 0.3; // kg CO2e per kg of waste

// 1. Calculate Baseline Emissions
const baselineElectricityEmissions = annualElectricityKwh * EF_ELECTRICITY;
const baselineCarEmissions = (annualCarMiles / carMpg) * EF_GASOLINE_GALLON;
const baselineDietEmissions = householdMembers * EF_AVG_DIET_PER_PERSON;
const baselineWasteEmissions = annualWasteKg * EF_WASTE_TO_LANDFILL;

const totalBaselineEmissions = baselineElectricityEmissions +
                               baselineCarEmissions +
                               baselineDietEmissions +
                               baselineWasteEmissions;

// 2. Calculate Potential Carbon Reduction (Savings)
const electricitySavings = annualElectricityKwh * (electricityReductionPercent / 100) * EF_ELECTRICITY;
const carSavings = (annualCarMiles / carMpg) * (carReductionPercent / 100) * EF_GASOLINE_GALLON;
// Diet savings are proportional to the shift, capped by DIET_MAX_REDUCTION_FACTOR
const dietSavings = baselineDietEmissions * (dietShiftPercent / 100) * DIET_MAX_REDUCTION_FACTOR;
const wasteSavings = annualWasteKg * (electricityReductionPercent / 100) * EF_WASTE_TO_LANDFILL;

const totalCarbonReductionKg = electricitySavings + carSavings + dietSavings + wasteSavings;

// 3. Calculate Percentage Reduction
const percentageReduction = totalBaselineEmissions > 0 ? (totalCarbonReductionKg / totalBaselineEmissions) * 100 : 0;

return {
  totalCarbonReductionKg: totalCarbonReductionKg,
  percentageReduction: percentageReduction
};
