function calculatePreparedness(inputs) {
  const maxPossiblePoints = 27; // Sum of max values for all inputs (5+3+3+4+2+3+2+2+2+1)

  let currentScore = 0;

  // Input validation and scoring
  currentScore += inputs.emergencyKitScore || 0;
  currentScore += inputs.communicationPlanStatus || 0;
  currentScore += inputs.evacuationPlanStatus || 0;
  currentScore += inputs.financialPreparednessScore || 0;
  currentScore += inputs.specialNeedsIntegration || 0;
  currentScore += inputs.hazardAwarenessAndMitigation || 0;
  currentScore += inputs.documentSecurity || 0;
  currentScore += inputs.trainingAndDrills || 0;
  currentScore += inputs.utilityPreparedness || 0;
  currentScore += inputs.communityInvolvement || 0;

  // Ensure score doesn't exceed max possible points (should be handled by input max values, but good for robustness)
  currentScore = Math.min(currentScore, maxPossiblePoints);

  const preparednessPercentage = (currentScore / maxPossiblePoints) * 100;

  let readinessLevel;
  if (preparednessPercentage < 25) {
    readinessLevel = 'Critical: Immediate Action Required';
  } else if (preparednessPercentage < 50) {
    readinessLevel = 'Basic: Significant Improvements Needed';
  } else if (preparednessPercentage < 75) {
    readinessLevel = 'Good: Solid Foundation, Room for Enhancement';
  } else if (preparednessPercentage < 90) {
    readinessLevel = 'Excellent: Well-Prepared';
  } else {
    readinessLevel = 'Superior: Exemplary Preparedness';
  }

  return {
    preparednessScore: currentScore,
    preparednessPercentage: preparednessPercentage,
    readinessLevel: readinessLevel
  };
}
