function calculate({
  numMembers,
  avgElectricityKWhMonth,
  targetElectricityReductionKWhMonth,
  meatMealsPerPersonPerWeek,
  targetVeggieMealsPerPersonPerWeek,
  wasteKgPerWeek,
  targetWasteReductionPct
}) {
  // Clamp inputs to reasonable minimums
  numMembers = Math.max(1, numMembers);
  avgElectricityKWhMonth = Math.max(0, avgElectricityKWhMonth);
  targetElectricityReductionKWhMonth = Math.max(0, targetElectricityReductionKWhMonth);
  meatMealsPerPersonPerWeek = Math.max(0, meatMealsPerPersonPerWeek);
  targetVeggieMealsPerPersonPerWeek = Math.max(0, targetVeggieMealsPerPersonPerWeek);
  wasteKgPerWeek = Math.max(0, wasteKgPerWeek);
  targetWasteReductionPct = Math.max(0, Math.min(100, targetWasteReductionPct)); // Percentage between 0-100

  // Emission Factors (Illustrative, typical averages)
  const EMISSION_ELECTRICITY_KG_CO2_PER_KWH = 0.4;
  const EMISSION_MEAT_MEAL_KG_CO2 = 3.0; // Average for a typical meat-heavy meal
  const EMISSION_WASTE_KG_CO2_PER_KG = 1.5; // From landfill methane, transport etc.

  // Cost Factors (Illustrative, typical averages)
  const COST_ELECTRICITY_PER_KWH = 0.15;
  const COST_SAVING_PER_VEGGIE_MEAL = 3.00; // Estimated saving per meal by switching from meat to veggie

  // Investment Costs (Illustrative)
  // Assuming for every 100 kWh/month reduction, a 1kW solar system might be involved, or significant efficiency upgrades.
  // This is a simplified cost model.
  const COST_PER_100KWH_REDUCTION_INVESTMENT = 2500; // e.g., solar, insulation, high-efficiency appliances
  const COST_COMPOSTING_SETUP = 100; // One-time cost for composting system

  // Conversions
  const WEEKS_PER_YEAR = 52;
  const MONTHS_PER_YEAR = 12;
  const KG_CO2_PER_TREE_PER_YEAR = 20; // Avg CO2 absorbed by one mature tree per year

  // 1. Electricity Reduction Calculation
  const actualElectricityReductionKWhMonth = Math.min(targetElectricityReductionKWhMonth, avgElectricityKWhMonth);
  const electricityCO2ReductionMonthly = actualElectricityReductionKWhMonth * EMISSION_ELECTRICITY_KG_CO2_PER_KWH;
  const electricityCostSavingsMonthly = actualElectricityReductionKWhMonth * COST_ELECTRICITY_PER_KWH;
  const electricityUpfrontCost = (actualElectricityReductionKWhMonth / 100) * COST_PER_100KWH_REDUCTION_INVESTMENT;

  // 2. Dietary Reduction Calculation
  const meatMealsReducedPerPersonPerWeek = Math.min(targetVeggieMealsPerPersonPerWeek, meatMealsPerPersonPerWeek);
  const dietCO2ReductionMonthly = meatMealsReducedPerPersonPerWeek * numMembers * (WEEKS_PER_YEAR / MONTHS_PER_YEAR) * EMISSION_MEAT_MEAL_KG_CO2;
  const dietCostSavingsMonthly = meatMealsReducedPerPersonPerWeek * numMembers * (WEEKS_PER_YEAR / MONTHS_PER_YEAR) * COST_SAVING_PER_VEGGIE_MEAL;

  // 3. Waste Reduction Calculation
  const wasteReducedKgPerWeek = wasteKgPerWeek * (targetWasteReductionPct / 100);
  const wasteCO2ReductionMonthly = wasteReducedKgPerWeek * (WEEKS_PER_YEAR / MONTHS_PER_YEAR) * EMISSION_WASTE_KG_CO2_PER_KG;
  // No direct monthly cost saving from waste reduction assumed, but there's an upfront cost for composting.
  const wasteUpfrontCost = targetWasteReductionPct > 0 && wasteKgPerWeek > 0 ? COST_COMPOSTING_SETUP : 0;

  // Total Annual Calculations
  const annualCO2Reduction = (electricityCO2ReductionMonthly + dietCO2ReductionMonthly + wasteCO2ReductionMonthly) * MONTHS_PER_YEAR;
  const annualCostSavings = (electricityCostSavingsMonthly + dietCostSavingsMonthly) * MONTHS_PER_YEAR;
  const totalUpfrontInvestment = electricityUpfrontCost + wasteUpfrontCost;

  // Payback Period
  let paybackPeriodYears = 0;
  if (totalUpfrontInvestment > 0 && annualCostSavings > 0) {
    paybackPeriodYears = totalUpfrontInvestment / annualCostSavings;
  } else if (totalUpfrontInvestment > 0 && annualCostSavings <= 0) {
    paybackPeriodYears = Infinity; // Will never pay back
  } else {
    paybackPeriodYears = 0; // No investment, no payback needed
  }

  // Equivalent Trees
  const equivalentTreesPlanted = annualCO2Reduction > 0 ? annualCO2Reduction / KG_CO2_PER_TREE_PER_YEAR : 0;

  return {
    annualCO2Reduction: annualCO2Reduction,
    annualCostSavings: annualCostSavings,
    paybackPeriodYears: paybackPeriodYears,
    equivalentTreesPlanted: equivalentTreesPlanted
  };
}
