function mapValueToScore(value, thresholds, scores) {
    if (value <= thresholds[0]) return scores[0];
    for (let i = 0; i < thresholds.length - 1; i++) {
        if (value > thresholds[i] && value <= thresholds[i+1]) {
            const progress = (value - thresholds[i]) / (thresholds[i+1] - thresholds[i]);
            return scores[i] + progress * (scores[i+1] - scores[i]);
        }
    }
    return scores[scores.length - 1];
}

const clamp = (num, min, max) => Math.min(Math.max(num, min), max);

const monthlyIncome = annualHouseholdIncome / 12;
const totalMonthlyExpenses = monthlyHousingCost + monthlyNonHousingExpenses + totalMonthlyDebtPayments;

// 1. Debt-to-Income (DTI) Score
// Higher DTI = higher stress
const dti = monthlyIncome > 0 ? (totalMonthlyDebtPayments / monthlyIncome) * 100 : 1000; // Assign very high DTI if no income for calculation purposes
const dtiScore = clamp(mapValueToScore(dti, [0, 15, 30, 43, 60, 1000], [0, 10, 35, 65, 85, 100]), 0, 100);

// 2. Emergency Savings Runway (ESR) Score
// Lower ESR = higher stress
const savingsRunwayMonths = totalMonthlyExpenses > 0 ? emergencySavings / totalMonthlyExpenses : (emergencySavings > 0 ? 999 : 0); // Handle div by zero; infinite if expenses are zero but savings exist, zero if no savings
const savingsScore = clamp(mapValueToScore(savingsRunwayMonths, [0, 1, 3, 6, 12, 999], [100, 80, 50, 20, 5, 0]), 0, 100);

// 3. Net Income Stability Score (NISS) - Disposable Income Ratio
// Lower/negative disposable income ratio = higher stress
const disposableIncome = monthlyIncome - totalMonthlyExpenses;
const netIncomeRatio = monthlyIncome > 0 ? disposableIncome / monthlyIncome : -1; // -1 if no income
const nissScore = clamp(mapValueToScore(netIncomeRatio, [-1, -0.5, 0, 0.1, 0.2, 1], [100, 90, 70, 40, 10, 0]), 0, 100);

// 4. Total Debt Burden (TDB) Score
// Higher TDB ratio = higher stress
const tdbRatio = annualHouseholdIncome > 0 ? totalHouseholdDebt / annualHouseholdIncome : 10; // Assign high ratio if no income
const tdbScore = clamp(mapValueToScore(tdbRatio, [0, 1, 2, 3, 5, 10], [0, 15, 45, 75, 90, 100]), 0, 100);

// 5. Regional Unemployment Rate (RUR) Score
// Higher unemployment rate = higher stress
const rurScore = clamp(mapValueToScore(regionalUnemploymentRate, [0, 3, 5, 7, 10, 15], [0, 10, 30, 60, 85, 100]), 0, 100);

// 6. Income Stability Factor (ISF) Score
// incomeStabilityFactor: 1 (very stable) to 5 (very unstable); higher factor = higher stress
const isfScore = clamp(mapValueToScore(incomeStabilityFactor, [1, 2, 3, 4, 5], [0, 25, 50, 75, 100]), 0, 100);

// Define weights for each component
const weights = {
    dti: 0.20,
    savings: 0.20,
    niss: 0.25,
    tdb: 0.15,
    rur: 0.10,
    isf: 0.10
};

// Calculate final stress index score as a weighted sum
let stressIndexScore = (
    dtiScore * weights.dti +
    savingsScore * weights.savings +
    nissScore * weights.niss +
    tdbScore * weights.tdb +
    rurScore * weights.rur +
    isfScore * weights.isf
);

stressIndexScore = clamp(stressIndexScore, 0, 100); // Ensure score is within 0-100

let stressCategory;
if (stressIndexScore <= 20) {
    stressCategory = "Low Stress";
} else if (stressIndexScore <= 40) {
    stressCategory = "Moderate Stress";
} else if (stressIndexScore <= 60) {
    stressCategory = "Elevated Stress";
} else if (stressIndexScore <= 80) {
    stressCategory = "High Stress";
} else {
    stressCategory = "Severe Stress";
}

return {
    stressIndexScore: parseFloat(stressIndexScore.toFixed(1)),
    stressCategory: stressCategory,
    debtToIncomeRatio: parseFloat(dti.toFixed(2)),
    savingsRunwayMonths: parseFloat(savingsRunwayMonths.toFixed(1))
};
