function calculateInflationImpact(inputs) {
    const currentMonthlyBudget = parseFloat(inputs.currentMonthlyBudget);
    const generalAnnualInflationRate = parseFloat(inputs.generalAnnualInflationRate) / 100;
    const projectionPeriodYears = parseInt(inputs.projectionPeriodYears);

    const housingCurrentMonthlySpend = parseFloat(inputs.housingCurrentMonthlySpend);
    const foodCurrentMonthlySpend = parseFloat(inputs.foodCurrentMonthlySpend);
    const transportCurrentMonthlySpend = parseFloat(inputs.transportCurrentMonthlySpend);
    const utilitiesCurrentMonthlySpend = parseFloat(inputs.utilitiesCurrentMonthlySpend);

    const housingInflationDifferential = parseFloat(inputs.housingInflationDifferential || 0) / 100;
    const foodInflationDifferential = parseFloat(inputs.foodInflationDifferential || 0) / 100;
    const transportInflationDifferential = parseFloat(inputs.transportInflationDifferential || 0) / 100;
    // Utilities and other default to general inflation if no differential is provided

    // Input validation and edge case handling
    if (isNaN(currentMonthlyBudget) || isNaN(generalAnnualInflationRate) || isNaN(projectionPeriodYears) ||
        isNaN(housingCurrentMonthlySpend) || isNaN(foodCurrentMonthlySpend) || isNaN(transportCurrentMonthlySpend) || isNaN(utilitiesCurrentMonthlySpend)) {
        return { error: "All required inputs must be valid numbers." };
    }

    if (currentMonthlyBudget < 0 || projectionPeriodYears < 0 || housingCurrentMonthlySpend < 0 || foodCurrentMonthlySpend < 0 || transportCurrentMonthlySpend < 0 || utilitiesCurrentMonthlySpend < 0) {
        return { error: "Budget, spend, and projection years cannot be negative." };
    }

    const sumSpecifiedSpends = housingCurrentMonthlySpend + foodCurrentMonthlySpend + transportCurrentMonthlySpend + utilitiesCurrentMonthlySpend;
    let otherCurrentMonthlySpend = currentMonthlyBudget - sumSpecifiedSpends;

    // Ensure otherCurrentMonthlySpend is not negative. If sumSpecifiedSpends exceeds budget, cap other to 0 and distribute the 'over-spend' proportionally
    if (otherCurrentMonthlySpend < 0) {
        // This indicates an invalid budget allocation where specified spends exceed the total budget.
        // For robust calculation, we will treat 'other' as 0 and adjust the currentMonthlyBudget upwards to match the sumSpecifiedSpends for calculation purposes,
        // or simply calculate based on the currentMonthlyBudget for total but keep 'other' as 0, which implies other categories are fully squeezed.
        // Let's go with the latter for simplicity in projection, acknowledging the input implies explicit categories consume/exceed the budget.
        otherCurrentMonthlySpend = 0; 
    }

    // Helper function to project future spend
    const projectSpend = (currentSpend, effectiveRate, years) => {
        if (years === 0) return currentSpend;
        return currentSpend * Math.pow(1 + effectiveRate, years);
    };

    // Calculate effective inflation rates for each category
    const housingEffectiveRate = generalAnnualInflationRate + housingInflationDifferential;
    const foodEffectiveRate = generalAnnualInflationRate + foodInflationDifferential;
    const transportEffectiveRate = generalAnnualInflationRate + transportInflationDifferential;
    const utilitiesEffectiveRate = generalAnnualInflationRate; // Uses general rate if no specific differential
    const otherEffectiveRate = generalAnnualInflationRate;

    // Project future spend for each category
    const projectedHousingMonthlySpend = projectSpend(housingCurrentMonthlySpend, housingEffectiveRate, projectionPeriodYears);
    const projectedFoodMonthlySpend = projectSpend(foodCurrentMonthlySpend, foodEffectiveRate, projectionPeriodYears);
    const projectedTransportMonthlySpend = projectSpend(transportCurrentMonthlySpend, transportEffectiveRate, projectionPeriodYears);
    const projectedUtilitiesMonthlySpend = projectSpend(utilitiesCurrentMonthlySpend, utilitiesEffectiveRate, projectionPeriodYears);
    const projectedOtherMonthlySpend = projectSpend(otherCurrentMonthlySpend, otherEffectiveRate, projectionPeriodYears);

    // Calculate total projected monthly budget
    const projectedTotalMonthlyBudget = projectedHousingMonthlySpend + projectedFoodMonthlySpend + projectedTransportMonthlySpend + projectedUtilitiesMonthlySpend + projectedOtherMonthlySpend;

    // Calculate increases
    const totalMonthlyBudgetIncrease = projectedTotalMonthlyBudget - currentMonthlyBudget;
    const totalMonthlyBudgetIncreasePercentage = currentMonthlyBudget > 0 ? (totalMonthlyBudgetIncrease / currentMonthlyBudget) * 100 : 0;

    const housingMonthlyIncrease = projectedHousingMonthlySpend - housingCurrentMonthlySpend;
    const foodMonthlyIncrease = projectedFoodMonthlySpend - foodCurrentMonthlySpend;
    const transportMonthlyIncrease = projectedTransportMonthlySpend - transportCurrentMonthlySpend;
    const utilitiesMonthlyIncrease = projectedUtilitiesMonthlySpend - utilitiesCurrentMonthlySpend;
    const otherMonthlyIncrease = projectedOtherMonthlySpend - otherCurrentMonthlySpend;

    return {
        projectedTotalMonthlyBudget,
        projectedHousingMonthlySpend,
        projectedFoodMonthlySpend,
        projectedTransportMonthlySpend,
        projectedUtilitiesMonthlySpend,
        projectedOtherMonthlySpend,
        totalMonthlyBudgetIncrease,
        totalMonthlyBudgetIncreasePercentage,
        housingMonthlyIncrease,
        foodMonthlyIncrease,
        transportMonthlyIncrease,
        utilitiesMonthlyIncrease,
        otherMonthlyIncrease
    };
}
