const { numMembers, avgShowerDuration, showersPerPersonPerWeek, toiletFlushesPerPersonPerDay, laundryLoadsPerWeek, dishwashingFrequency, dietType, monthlyNonFoodSpend } = inputs;

// Constants for water usage (liters)
const DAYS_IN_YEAR = 365;
const LITERS_PER_SHOWER_MINUTE = 12; // Average shower head
const LITERS_PER_TOILET_FLUSH = 7; // Modern toilet
const LITERS_PER_LAUNDRY_LOAD = 50; // HE washing machine
const LITERS_PER_DISHWASHER_LOAD = 15; // Efficient dishwasher

// Indirect water usage constants (approximate annual liters per person)
const DIET_WATER_FOOTPRINT_PER_DAY = {
  'meat-heavy': 5500, // liters/day/person
  'balanced': 3500,   // liters/day/person
  'vegetarian': 2500, // liters/day/person
  'vegan': 1500,      // liters/day/person
  'default': 3500     // Fallback for unrecognized diet type
};

// Goods water footprint (very rough estimate: liters per USD spent)
const LITERS_PER_USD_SPENT_ON_GOODS = 100;

// Direct Water Footprint Calculation
const showersWater = numMembers * showersPerPersonPerWeek * avgShowerDuration * LITERS_PER_SHOWER_MINUTE * (DAYS_IN_YEAR / 7);
const toiletWater = numMembers * toiletFlushesPerPersonPerDay * LITERS_PER_TOILET_FLUSH * DAYS_IN_YEAR;
const laundryWater = laundryLoadsPerWeek * LITERS_PER_LAUNDRY_LOAD * (DAYS_IN_YEAR / 7);
const dishwashingWater = dishwashingFrequency * LITERS_PER_DISHWASHER_LOAD * (DAYS_IN_YEAR / 7);

const annualDirectWaterFootprint = showersWater + toiletWater + laundryWater + dishwashingWater;

// Indirect Water Footprint Calculation
const dietWaterPerDay = DIET_WATER_FOOTPRINT_PER_DAY[dietType.toLowerCase()] || DIET_WATER_FOOTPRINT_PER_DAY['default'];
const annualDietWater = numMembers * dietWaterPerDay * DAYS_IN_YEAR;
const annualGoodsWater = monthlyNonFoodSpend * 12 * LITERS_PER_USD_SPENT_ON_GOODS;

const annualIndirectWaterFootprint = annualDietWater + annualGoodsWater;

// Total Water Footprint
const totalAnnualWaterFootprint = annualDirectWaterFootprint + annualIndirectWaterFootprint;

return {
  totalWaterFootprint: totalAnnualWaterFootprint,
  directWaterFootprint: annualDirectWaterFootprint,
  indirectWaterFootprint: annualIndirectWaterFootprint
};
