let currentBalance = inputs.initialBalance;
const annualReturnFactor = 1 + (inputs.annualReturnRate / 100);
const inflationFactor = 1 + (inputs.inflationRate / 100);

let totalNewContributionsAdded = 0;
let actualWithdrawalsMade = 0;

for (let year = 1; year <= inputs.yearsToGrow; year++) {
    // Add annual contribution
    currentBalance += inputs.annualContribution;
    totalNewContributionsAdded += inputs.annualContribution;

    // Apply investment growth
    currentBalance *= annualReturnFactor;

    // Apply withdrawals if applicable
    if (inputs.annualWithdrawalAmount > 0 && year >= inputs.withdrawalStartYear && currentBalance > 0) {
        const withdrawalAmountThisYear = inputs.annualWithdrawalAmount;
        const actualWithdrawal = Math.min(currentBalance, withdrawalAmountThisYear);
        currentBalance -= actualWithdrawal;
        actualWithdrawalsMade += actualWithdrawal;
    }

    currentBalance = Math.max(0, currentBalance); // Ensure balance never goes below zero
}

const nominalFutureValue = currentBalance;

// Total contributions: just the sum of annual contributions during the period
const totalContributionsSum = totalNewContributionsAdded;

// Total earnings: (Final Balance - Initial Balance - Total New Contributions Added) + Total Withdrawals
// This formula accounts for withdrawals coming potentially from earnings or reducing the effective principal.
const totalEarnings = nominalFutureValue - inputs.initialBalance - totalContributionsSum + actualWithdrawalsMade;

// Calculate real future value by discounting nominal future value by cumulative inflation
const realFutureValue = nominalFutureValue / Math.pow(inflationFactor, inputs.yearsToGrow);

return {
    nominalFutureValue: nominalFutureValue,
    realFutureValue: realFutureValue,
    totalContributions: totalContributionsSum,
    totalEarnings: totalEarnings
};
