function calculateEfficiency(inputs) {
  const route_distance_km = Math.max(0, inputs.route_distance_km);
  const border_crossing_points = Math.max(0, inputs.border_crossing_points);
  const avg_border_processing_time_hours = Math.max(0, inputs.avg_border_processing_time_hours);
  const infrastructure_quality_index = Math.max(1, Math.min(5, inputs.infrastructure_quality_index));
  const security_risk_index = Math.max(1, Math.min(5, inputs.security_risk_index));
  const demand_urgency_factor = Math.max(1, Math.min(5, inputs.demand_urgency_factor));
  const local_logistics_capacity = Math.max(1, Math.min(5, inputs.local_logistics_capacity));
  const volume_of_aid_kg = Math.max(0, inputs.volume_of_aid_kg);

  let bottleneck_points = 0;
  let bonus_points = 0;

  // 1. Distance Impact: Penalty for longer routes
  //   - 2 points deduction for every 100 km. Max deduction around 50 points for 2500km
  bottleneck_points += (route_distance_km / 100) * 2;

  // 2. Border/Checkpoint Impact: Penalty for more crossings and longer processing times
  //   - 4 points deduction for every 12 hours of processing time per border.
  bottleneck_points += border_crossing_points * (avg_border_processing_time_hours / 12) * 4;

  // 3. Infrastructure Quality Impact: Penalty for poor infrastructure
  //   - (6 - index) * 4 points. (1=Very Poor -> 20 pts, 5=Excellent -> 4 pts)
  bottleneck_points += (6 - infrastructure_quality_index) * 4;

  // 4. Security Risk Impact: Penalty for higher security risks
  //   - index * 5 points. (1=Very Low -> 5 pts, 5=Extreme -> 25 pts)
  bottleneck_points += security_risk_index * 5;

  // 5. Demand Urgency Impact: Higher urgency can expose system weaknesses
  //   - (index - 1) * 3 points. (1=Low -> 0 pts, 5=Critical -> 12 pts)
  bottleneck_points += (demand_urgency_factor - 1) * 3;

  // 6. Volume Complexity Impact: Logarithmic penalty for larger volumes (more complex to handle)
  //   - Base: 10,000 kg. log10(volume/10000) * 5 points.
  bottleneck_points += Math.log10(Math.max(1, volume_of_aid_kg / 10000)) * 5;

  // 7. Local Logistics Capacity Benefit: Bonus for strong local capacity
  //   - (index - 1) * 4 points. (1=Very Limited -> 0 pts, 5=High -> 16 pts)
  bonus_points += (local_logistics_capacity - 1) * 4;

  // Base score starts at 100, then apply deductions and bonuses
  let raw_score = 100 - bottleneck_points + bonus_points;

  // Cap the score between 0 and 100
  const efficiency_score = Math.max(0, Math.min(100, raw_score));

  return {
    efficiency_score: efficiency_score
  };
}
