function calculate({ population_in_need, aid_volume_available, logistical_capacity, average_transport_cost_per_ton, risk_factor_conflict_zones, aid_duration_days_target, basic_aid_per_person_kg_per_day, shelter_kits_available }) {

  // Input validation and default to 0 for non-negative inputs
  population_in_need = Math.max(0, population_in_need || 0);
  aid_volume_available = Math.max(0, aid_volume_available || 0);
  logistical_capacity = Math.max(0, logistical_capacity || 0);
  average_transport_cost_per_ton = Math.max(0, average_transport_cost_per_ton || 0);
  risk_factor_conflict_zones = Math.max(0, Math.min(1, risk_factor_conflict_zones || 0)); // Cap between 0 and 1
  aid_duration_days_target = Math.max(1, aid_duration_days_target || 1); // Minimum 1 day
  basic_aid_per_person_kg_per_day = Math.max(0.01, basic_aid_per_person_kg_per_day || 0.01); // Minimum small positive value to prevent division by zero
  shelter_kits_available = Math.max(0, shelter_kits_available || 0);

  let total_required_aid_tons_for_duration = 0;
  if (population_in_need > 0) {
    const total_required_aid_kg_for_duration = population_in_need * basic_aid_per_person_kg_per_day * aid_duration_days_target;
    total_required_aid_tons_for_duration = total_required_aid_kg_for_duration / 1000;
  }

  const max_logistically_distributable_tons = logistical_capacity * aid_duration_days_target;

  // Determine actual aid distributed, limited by availability, need, and logistics
  let total_aid_distributed_tons = Math.min(aid_volume_available,
                                           total_required_aid_tons_for_duration,
                                           max_logistically_distributable_tons);

  // Ensure total_aid_distributed_tons is non-negative
  total_aid_distributed_tons = Math.max(0, total_aid_distributed_tons);

  // Calculate estimated total cost
  const base_transport_cost = total_aid_distributed_tons * average_transport_cost_per_ton;
  const risk_multiplier = 1 + risk_factor_conflict_zones;
  const estimated_total_cost = base_transport_cost * risk_multiplier;

  // Calculate Aid Reach Percentage
  let aid_reach_percentage = 0;
  if (total_required_aid_tons_for_duration > 0) {
    aid_reach_percentage = (total_aid_distributed_tons / total_required_aid_tons_for_duration) * 100;
  }
  aid_reach_percentage = Math.min(aid_reach_percentage, 100); // Cap at 100%

  // Calculate Effective Aid Duration
  let effective_aid_duration_days = 0;
  if (population_in_need > 0) {
    effective_aid_duration_days = (total_aid_distributed_tons * 1000) / (population_in_need * basic_aid_per_person_kg_per_day);
  }
  // The effective duration cannot exceed the target duration for the full population if the aid volume is sufficient.
  // It shows how long the distributed aid *could* sustain the *entire* population_in_need.
  effective_aid_duration_days = Math.min(effective_aid_duration_days, aid_duration_days_target); // If distributed aid exceeds required for target duration, still cap at target duration.
  effective_aid_duration_days = Math.max(0, effective_aid_duration_days);

  // Calculate Shelter Kits Distributed (assuming 5 people per kit as a heuristic)
  const people_per_shelter_kit = 5;
  const max_kits_needed = (population_in_need > 0 && people_per_shelter_kit > 0) ? Math.ceil(population_in_need / people_per_shelter_kit) : 0;
  const shelter_kits_distributed = Math.min(shelter_kits_available, max_kits_needed);

  return {
    aid_reach_percentage: aid_reach_percentage,
    total_aid_distributed_tons: total_aid_distributed_tons,
    estimated_total_cost: estimated_total_cost,
    effective_aid_duration_days: effective_aid_duration_days,
    shelter_kits_distributed: shelter_kits_distributed
  };
}
