function calculateROI(inputs) {
  const { projectFunding, projectDuration, directBeneficiaries, socialImpactMultiplier, economicImpactMultiplier, riskAdjustmentFactor, overheadCostsPercentage, sustainabilityEffectiveness } = inputs;

  // Handle edge cases for inputs
  if (projectFunding <= 0 || projectDuration <= 0 || directBeneficiaries <= 0) {
    return {
      totalProjectCosts: 0,
      totalSocialValue: 0,
      totalEconomicValue: 0,
      combinedTotalValue: 0,
      netCombinedReturn: 0,
      combinedROI: 0,
      valuePerBeneficiary: 0
    };
  }

  // Step 1: Calculate Total Project Costs
  // Includes initial funding plus annual overheads over the project duration
  const annualOverheadCost = projectFunding * (overheadCostsPercentage / 100);
  const totalOverheadCosts = annualOverheadCost * projectDuration;
  const totalProjectCosts = projectFunding + totalOverheadCosts;

  // Step 2: Establish a Baseline Value per Beneficiary per Year
  // This is a heuristic representing a basic level of improved well-being or economic activity possible through aid.
  // It's a foundational assumption for the model to quantify impact in monetary terms.
  const baseValuePerBeneficiaryPerYear = 2500; // USD/person/year (e.g., basic needs met, productivity gain potential)

  // Step 3: Calculate Effective Project Duration including Sustainability
  // The sustainability factor extends the 'effective' duration of impact, capped to prevent infinite growth.
  // We assume sustainability extends impact by up to 50% of the original duration, scaled by effectiveness.
  const sustainabilityExtensionFactor = (sustainabilityEffectiveness / 100) * 0.5; // Max 50% extension
  const effectiveDuration = projectDuration * (1 + sustainabilityExtensionFactor);

  // Step 4: Calculate Potential Total Social and Economic Values
  // These are the values before accounting for risks, based on baseline, multipliers, and effective duration.
  const potentialTotalSocialValue = directBeneficiaries * baseValuePerBeneficiaryPerYear * socialImpactMultiplier * effectiveDuration;
  const potentialTotalEconomicValue = directBeneficiaries * baseValuePerBeneficiaryPerYear * economicImpactMultiplier * effectiveDuration;

  // Step 5: Apply Risk Adjustment Factor
  // Reduces the potential value based on the perceived risk in the operational environment.
  const actualSocialValue = potentialTotalSocialValue * (1 - (riskAdjustmentFactor / 100));
  const actualEconomicValue = potentialTotalEconomicValue * (1 - (riskAdjustmentFactor / 100));

  // Step 6: Calculate Combined Total Value Generated
  const combinedTotalValue = actualSocialValue + actualEconomicValue;

  // Step 7: Calculate Net Combined Return
  // This is the total value generated minus the total costs incurred.
  const netCombinedReturn = combinedTotalValue - totalProjectCosts;

  // Step 8: Calculate Combined Return on Investment (ROI)
  // Handle division by zero if totalProjectCosts is zero (though initial check covers projectFunding <= 0)
  const combinedROI = totalProjectCosts > 0 ? (netCombinedReturn / totalProjectCosts) * 100 : 0;

  // Step 9: Calculate Value Generated Per Beneficiary
  const valuePerBeneficiary = directBeneficiaries > 0 ? combinedTotalValue / directBeneficiaries : 0;

  return {
    totalProjectCosts: totalProjectCosts,
    totalSocialValue: actualSocialValue,
    totalEconomicValue: actualEconomicValue,
    combinedTotalValue: combinedTotalValue,
    netCombinedReturn: netCombinedReturn,
    combinedROI: combinedROI,
    valuePerBeneficiary: valuePerBeneficiary
  };
}
