const { airflow_cfm, target_velocity_fpm, duct_type, aspect_ratio, duct_length_ft, allowable_friction_loss_per_100ft } = inputs;

// Input validation and default values
if (airflow_cfm <= 0 || target_velocity_fpm <= 0) {
    throw new Error('Airflow and target velocity must be positive values.');
}

// 1. Calculate Required Area
let required_duct_area_sqft = airflow_cfm / target_velocity_fpm;
let required_duct_area_sqin = required_duct_area_sqft * 144;

let equivalent_diameter_inches = 0;
let duct_width_inches = 0;
let duct_height_inches = 0;

const lowerCaseDuctType = duct_type ? duct_type.toLowerCase() : '';

if (lowerCaseDuctType === 'round') {
  // For round duct
  equivalent_diameter_inches = Math.sqrt(required_duct_area_sqin / Math.PI) * 2;
  // For consistency, set rectangular dimensions to 0 when round is selected
  duct_width_inches = 0;
  duct_height_inches = 0;
} else if (lowerCaseDuctType === 'rectangular') {
  // Ensure aspect_ratio is provided for rectangular ducts and is reasonable
  const effective_aspect_ratio = (aspect_ratio && aspect_ratio > 0) ? Math.max(0.25, Math.min(4, aspect_ratio)) : 1.0; // Default to square if not provided or out of range

  duct_height_inches = Math.sqrt(required_duct_area_sqin / effective_aspect_ratio);
  duct_width_inches = duct_height_inches * effective_aspect_ratio;

  // Calculate equivalent diameter for rectangular duct (for reference/friction loss if needed elsewhere)
  // Using the common approximation for friction equivalence: De = 2*W*H / (W+H)
  equivalent_diameter_inches = (2 * duct_width_inches * duct_height_inches) / (duct_width_inches + duct_height_inches);

} else {
    throw new Error('Please specify duct shape as "round" or "rectangular".');
}

// Ensure dimensions are positive (min 1 inch for practical reasons)
equivalent_diameter_inches = Math.max(1, equivalent_diameter_inches);
duct_width_inches = Math.max(1, duct_width_inches);
duct_height_inches = Math.max(1, duct_height_inches);

// 2. Calculate Actual Velocity based on calculated dimensions
let actual_area_sqin;
if (lowerCaseDuctType === 'round') {
    actual_area_sqin = Math.PI * (equivalent_diameter_inches / 2) ** 2;
} else {
    actual_area_sqin = duct_width_inches * duct_height_inches;
}
let actual_velocity_fpm = (airflow_cfm * 144) / actual_area_sqin;

// 3. Calculate Total Friction Loss
// The input `allowable_friction_loss_per_100ft` is a rate.
let total_friction_loss_inwg = (allowable_friction_loss_per_100ft / 100) * duct_length_ft;

// Cap total friction loss to a reasonable maximum for typical HVAC systems
total_friction_loss_inwg = Math.min(2.0, total_friction_loss_inwg); // e.g., max 2.0 in.w.g. for typical systems

return {
  equivalent_diameter_inches,
  duct_width_inches,
  duct_height_inches,
  total_friction_loss_inwg,
  actual_velocity_fpm
};
