const Q = inputs.flow_rate_gpm;
const D = inputs.pipe_diameter_in;
const L = inputs.equivalent_pipe_length_ft;
const C = inputs.hazen_williams_c;
const initial_pressure_psi = inputs.initial_pressure_psi;
const elevation_change_ft = inputs.elevation_change_ft;

// Constants
const PSI_PER_FOOT_WATER = 0.433; // Approximately, 1 psi = 2.31 ft of water

let pressure_loss_psi = 0;
let final_pressure_psi = initial_pressure_psi;

if (Q <= 0 || D <= 0 || L <= 0 || C <= 0) {
    // Handle invalid inputs gracefully, returning 0 for loss and initial for final after elevation adjustment
    pressure_loss_psi = 0;
    final_pressure_psi = initial_pressure_psi - (elevation_change_ft * PSI_PER_FOOT_WATER);
} else {
    // Hazen-Williams formula for head loss (hf) in feet (NFPA 13 often uses 1.85, 4.87, 4.87 for these exponents)
    // The specific coefficients vary slightly by region/standard (0.00063 is common for Q in GPM, D in inches, L in feet)
    const head_loss_ft = 0.00063 * L * Math.pow(100 / C, 1.852) * Math.pow(Q, 1.852) / Math.pow(D, 4.8655);

    // Convert head loss from feet to psi
    pressure_loss_psi = head_loss_ft * PSI_PER_FOOT_WATER;

    // Calculate final pressure, accounting for friction loss and elevation change
    // Positive elevation_change_ft means the end is higher, causing a pressure drop.
    final_pressure_psi = initial_pressure_psi - pressure_loss_psi - (elevation_change_ft * PSI_PER_FOOT_WATER);

    // Ensure pressure loss is not negative (it's always a loss)
    pressure_loss_psi = Math.max(0, pressure_loss_psi);
}

return {
    pressure_loss_psi: pressure_loss_psi,
    final_pressure_psi: final_pressure_psi
};
