function getWaterDensity(tempF) {
    const densities = [
        [32, 62.418], [40, 62.428], [60, 62.368], [80, 62.223], [100, 62.000],
        [120, 61.700], [140, 61.334], [160, 60.900], [180, 60.400], [200, 60.120],
        [220, 59.730], [250, 58.740]
    ];

    if (tempF < densities[0][0]) return densities[0][1];
    if (tempF > densities[densities.length - 1][0]) return densities[densities.length - 1][1];

    for (let i = 0; i < densities.length - 1; i++) {
        const [t1, d1] = densities[i];
        const [t2, d2] = densities[i + 1];

        if (tempF >= t1 && tempF <= t2) {
            return d1 + (d2 - d1) * ((tempF - t1) / (t2 - t1));
        }
    }
    return densities[densities.length - 1][1];
}

const systemVolume = inputs.systemVolume;
const minTemp = inputs.minTemp;
const maxTemp = inputs.maxTemp;
const initialPressure = inputs.initialPressure;
const maxPressure = inputs.maxPressure;

// --- Input Validations ---
if (systemVolume <= 0) throw new Error("System Volume must be greater than 0.");
if (minTemp >= maxTemp) throw new Error("Maximum System Temperature must be greater than Minimum System Temperature.");
if (initialPressure <= 0) throw new Error("Initial Fill Pressure must be greater than 0.");
if (maxPressure <= initialPressure) throw new Error("Maximum Allowable Pressure must be greater than Initial Fill Pressure.");

const ATMOSPHERIC_PRESSURE_PSI = 14.7;

// Absolute pressures
const P_initial_abs = initialPressure + ATMOSPHERIC_PRESSURE_PSI;
const P_max_abs = maxPressure + ATMOSPHERIC_PRESSURE_PSI;

// Get water densities at min and max temperatures
const rhoMin = getWaterDensity(minTemp);
const rhoMax = getWaterDensity(maxTemp);

// Expansion Factor (E) - Fractional increase in volume of water
// E = (Volume at max temp - Volume at min temp) / Volume at min temp
// Since volume is inversely proportional to density: E = (rho_min / rho_max) - 1
const E = (rhoMin / rhoMax) - 1;

if (E <= 0) {
    // If temperatures are equal or density function is flat for a range.
    // In real systems, if maxTemp > minTemp, E must be > 0. This error catches issues with input or density data.
    if (maxTemp > minTemp) {
        throw new Error("Calculated Expansion Factor (E) is not positive for increasing temperature. Check density data or temperature range.");
    } else {
        return { requiredTankVolume: 0, waterExpansion: 0, acceptanceFactor: 0 };
    }
}

// Acceptance Factor (AF) - The fraction of the tank's volume that can accept expanded water
// For bladder/diaphragm tanks: AF = 1 - (P_initial_abs / P_max_abs)
const AF = 1 - (P_initial_abs / P_max_abs);

if (AF <= 0) {
    throw new Error("Acceptance Factor (AF) is not positive. Ensure Maximum Allowable Pressure is sufficiently higher than Initial Fill Pressure.");
}

// Calculate the volume of expanded water
const expandedWaterVolume = systemVolume * E;

// Calculate the required tank volume
const requiredTankVolume = expandedWaterVolume / AF;

return {
    requiredTankVolume: requiredTankVolume,
    waterExpansion: expandedWaterVolume,
    acceptanceFactor: AF
};
