function calculate({ roofAreaSqFt, iceThicknessInches, snowDepthInches, iceDensityPcf, snowDensityPcf, designSnowLiveLoadPsf, safetyFactor }) {
    // Input validation and sanitization
    roofAreaSqFt = Math.max(0, parseFloat(roofAreaSqFt) || 0);
    iceThicknessInches = Math.max(0, parseFloat(iceThicknessInches) || 0);
    snowDepthInches = Math.max(0, parseFloat(snowDepthInches) || 0);
    iceDensityPcf = Math.max(0, parseFloat(iceDensityPcf) || 0);
    snowDensityPcf = Math.max(0, parseFloat(snowDensityPcf) || 0);
    designSnowLiveLoadPsf = Math.max(0, parseFloat(designSnowLiveLoadPsf) || 0);
    safetyFactor = Math.max(1, parseFloat(safetyFactor) || 1.0); // Safety factor must be at least 1

    let iceLoadPsf = 0;
    let snowLoadPsf = 0;
    let totalAppliedLoadPsf = 0;
    let totalAppliedWeightLbs = 0;
    let effectiveDesignCapacityPsf = 0;
    let loadRatio = 0;
    let stressAssessment = "Insufficient Data or Inputs Invalid";

    if (roofAreaSqFt === 0 || designSnowLiveLoadPsf === 0 || safetyFactor < 1) {
        return {
            iceLoadPsf: 0,
            snowLoadPsf: 0,
            totalAppliedLoadPsf: 0,
            totalAppliedWeightLbs: 0,
            effectiveDesignCapacityPsf: 0,
            loadRatio: 0,
            stressAssessment: "Please provide valid positive inputs for Roof Area, Design Load, and a Safety Factor >= 1."
        };
    }

    // 1. Calculate ice load per square foot
    const iceThicknessFt = iceThicknessInches / 12;
    iceLoadPsf = iceThicknessFt * iceDensityPcf;

    // 2. Calculate snow load per square foot
    const snowDepthFt = snowDepthInches / 12;
    snowLoadPsf = snowDepthFt * snowDensityPcf;

    // 3. Calculate total applied load per square foot
    totalAppliedLoadPsf = iceLoadPsf + snowLoadPsf;

    // 4. Calculate total applied weight on the entire roof
    totalAppliedWeightLbs = totalAppliedLoadPsf * roofAreaSqFt;

    // 5. Calculate effective design capacity considering the safety factor
    effectiveDesignCapacityPsf = designSnowLiveLoadPsf / safetyFactor;

    // 6. Calculate Load-to-Capacity Ratio
    if (effectiveDesignCapacityPsf > 0) {
        loadRatio = totalAppliedLoadPsf / effectiveDesignCapacityPsf;
    } else {
        loadRatio = Infinity; // If effective capacity is zero, ratio is infinite (critical scenario)
    }

    // 7. Assess Stress Risk
    if (loadRatio < 0.8) {
        stressAssessment = "Low Risk: Well within design limits.";
    } else if (loadRatio >= 0.8 && loadRatio < 1.0) {
        stressAssessment = "Moderate Risk: Approaching design limits. Monitor closely.";
    } else if (loadRatio >= 1.0 && loadRatio < 1.2) {
        stressAssessment = "High Risk: Exceeds effective capacity. Immediate action (e.g., snow/ice removal, structural inspection) recommended.";
    } else { // loadRatio >= 1.2
        stressAssessment = "Critical Risk: Significantly exceeds capacity. Emergency measures and professional assessment required.";
    }

    return {
        iceLoadPsf,
        snowLoadPsf,
        totalAppliedLoadPsf,
        totalAppliedWeightLbs,
        effectiveDesignCapacityPsf,
        loadRatio,
        stressAssessment
    };
}
