function calculate(inputs) {
  const productValuePerUnit = Number(inputs.productValuePerUnit) || 0;
  const numberOfUnits = Number(inputs.numberOfUnits) || 0;
  const tariffRate = Number(inputs.tariffRate) || 0;
  const shippingCostPerUnit = Number(inputs.shippingCostPerUnit) || 0;
  const insuranceRate = Number(inputs.insuranceRate) || 0;
  const customsBrokerageFeeRate = Number(inputs.customsBrokerageFeeRate) || 0;
  const customsBrokerageFeeFixed = Number(inputs.customsBrokerageFeeFixed) || 0;
  const administrativeOverheadFixed = Number(inputs.administrativeOverheadFixed) || 0;
  const supplyChainReRoutingCostPerUnit = Number(inputs.supplyChainReRoutingCostPerUnit) || 0;
  const currencySymbol = inputs.currencySymbol || '$';

  // Input validation and basic constraints
  if (productValuePerUnit < 0 || numberOfUnits < 0 || tariffRate < 0 || shippingCostPerUnit < 0 || insuranceRate < 0 || customsBrokerageFeeRate < 0 || customsBrokerageFeeFixed < 0 || administrativeOverheadFixed < 0 || supplyChainReRoutingCostPerUnit < 0) {
    throw new Error("All input values must be non-negative.");
  }
  if (tariffRate > 100 || insuranceRate > 100 || customsBrokerageFeeRate > 100) {
      throw new Error("Percentage rates cannot exceed 100%.");
  }

  // 1. Calculate Total Product Value
  const totalProductValue = productValuePerUnit * numberOfUnits;

  // 2. Calculate Tariff Duties
  const totalTariffDuties = totalProductValue * (tariffRate / 100);

  // 3. Calculate Shipping & Insurance Costs
  const totalShippingCost = shippingCostPerUnit * numberOfUnits;
  const totalInsuranceCost = totalProductValue * (insuranceRate / 100);
  const totalShippingInsuranceCosts = totalShippingCost + totalInsuranceCost;

  // 4. Calculate Customs Brokerage Fees
  let totalCustomsBrokerageFees = 0;
  if (customsBrokerageFeeRate > 0) {
    totalCustomsBrokerageFees += totalProductValue * (customsBrokerageFeeRate / 100);
  }
  totalCustomsBrokerageFees += customsBrokerageFeeFixed; // Add fixed fee regardless of rate

  // 5. Calculate Total Administrative Overhead
  const totalAdministrativeOverhead = administrativeOverheadFixed; // This output specifically for the 'other fixed admin'

  // 6. Calculate Supply Chain Re-routing Costs
  const totalSupplyChainReRoutingCosts = supplyChainReRoutingCostPerUnit * numberOfUnits;

  // 7. Calculate Grand Total Import Cost (Landed Cost)
  const grandTotalImportCost = totalProductValue +
                               totalTariffDuties +
                               totalShippingInsuranceCosts +
                               totalCustomsBrokerageFees +
                               administrativeOverheadFixed + // This is added to the grand total, but `totalAdministrativeOverhead` output only shows the fixed portion
                               totalSupplyChainReRoutingCosts;

  return {
    totalProductValue: totalProductValue.toFixed(2),
    totalTariffDuties: totalTariffDuties.toFixed(2),
    totalShippingInsuranceCosts: totalShippingInsuranceCosts.toFixed(2),
    totalCustomsBrokerageFees: totalCustomsBrokerageFees.toFixed(2),
    totalAdministrativeOverhead: administrativeOverheadFixed.toFixed(2), // Only output the fixed portion for this field
    totalSupplyChainReRoutingCosts: totalSupplyChainReRoutingCosts.toFixed(2),
    grandTotalImportCost: grandTotalImportCost.toFixed(2),
    currencySymbol: currencySymbol
  };
}
