function calculateTaxOptimization(inputs) {
    const grossIncome = inputs.grossIncome;
    const filingStatus = inputs.filingStatus.toUpperCase();
    const numberOfChildren = inputs.numberOfChildren;
    const qualifiedMedicalExpenses = inputs.qualifiedMedicalExpenses;
    const stateLocalPropertyIncomeTax = inputs.stateLocalPropertyIncomeTax;
    const homeMortgageInterestPaid = inputs.homeMortgageInterestPaid;
    const charitableCashContributions = inputs.charitableCashContributions;
    const charitableNonCashContributions = inputs.charitableNonCashContributions;
    const otherItemizedDeductions = inputs.otherItemizedDeductions;
    const educationExpensesForCredits = inputs.educationExpensesForCredits;

    // --- 2026 Estimated Standard Deductions (for illustrative purposes) ---
    let standardDeduction;
    switch (filingStatus) {
        case 'SINGLE':
            standardDeduction = 14600;
            break;
        case 'MFJ': // Married Filing Jointly
            standardDeduction = 29200;
            break;
        case 'HOH': // Head of Household
            standardDeduction = 21900;
            break;
        case 'MFS': // Married Filing Separately
            standardDeduction = 14600;
            break;
        case 'QW': // Qualifying Widow(er)
            standardDeduction = 29200;
            break;
        default:
            return { error: 'Invalid filing status. Please use Single, MFJ, HOH, MFS, or QW.' };
    }

    // --- Itemized Deduction Calculation ---
    let totalItemizedDeductions = 0;

    // Medical Expenses: Only the amount exceeding 7.5% of AGI (grossIncome for this calc)
    const medicalExpenseAGIFloor = grossIncome * 0.075;
    const deductibleMedicalExpenses = Math.max(0, qualifiedMedicalExpenses - medicalExpenseAGIFloor);
    totalItemizedDeductions += deductibleMedicalExpenses;

    // State and Local Taxes (SALT) Cap: $10,000 ($5,000 for MFS)
    const saltCap = (filingStatus === 'MFS') ? 5000 : 10000;
    const deductibleSALT = Math.min(stateLocalPropertyIncomeTax, saltCap);
    totalItemizedDeductions += deductibleSALT;

    // Home Mortgage Interest
    totalItemizedDeductions += homeMortgageInterestPaid;

    // Charitable Contributions (Simplified: assume within AGI limits for public charities)
    // Cash contributions generally limited to 60% of AGI, non-cash to 50% of AGI.
    // For this calculator, we sum them, assuming they broadly qualify within limits.
    const totalCharitableContributions = charitableCashContributions + charitableNonCashContributions;
    // Implement basic AGI limit, e.g., 60% of AGI for cash, 50% for non-cash, combined often 60% with carryover.
    // For simplification, let's assume a combined 60% AGI cap for charitable deductions here.
    const charitableDeductionCap = grossIncome * 0.60;
    totalItemizedDeductions += Math.min(totalCharitableContributions, charitableDeductionCap);

    // Other Qualified Itemized Deductions
    totalItemizedDeductions += otherItemizedDeductions;

    // --- Optimal Deduction Choice ---
    const optimalDeductionAmount = Math.max(standardDeduction, totalItemizedDeductions);

    // --- Taxable Income Calculation ---
    const estimatedTaxableIncome = Math.max(0, grossIncome - optimalDeductionAmount);

    // --- Federal Tax Credits (Simplified) ---
    let totalFederalTaxCredits = 0;

    // Child Tax Credit (CTC): $2,000 per qualifying child (2026 estimate, ignore phase-outs for this calc)
    totalFederalTaxCredits += numberOfChildren * 2000;

    // Education Credits (AOTC/LLC): Assume a simplified fixed amount if expenses exist
    if (educationExpensesForCredits > 0) {
        // Example: Assume eligibility for a combined $1,500 credit if expenses are significant.
        // In reality, AOTC max $2500, LLC max $2000, and income phase-outs apply.
        totalFederalTaxCredits += Math.min(educationExpensesForCredits * 0.5, 1500); // Simple illustration
    }

    // --- Estimated Federal Tax Liability (2026 Simplified Brackets) ---
    let federalTaxBeforeCredits = 0;

    // Using a simplified tax bracket system for illustration
    // These are *estimated* 2026 brackets and highly simplified.
    const brackets = {
        'SINGLE': [
            { limit: 11600, rate: 0.10 },
            { limit: 47150, rate: 0.12 },
            { limit: 100525, rate: 0.22 },
            { limit: Infinity, rate: 0.24 }
        ],
        'MFJ': [
            { limit: 23200, rate: 0.10 },
            { limit: 94300, rate: 0.12 },
            { limit: 201050, rate: 0.22 },
            { limit: Infinity, rate: 0.24 }
        ],
        'HOH': [
            { limit: 16550, rate: 0.10 },
            { limit: 63550, rate: 0.12 },
            { limit: 100500, rate: 0.22 },
            { limit: Infinity, rate: 0.24 }
        ],
        'MFS': [
            { limit: 11600, rate: 0.10 },
            { limit: 47150, rate: 0.12 },
            { limit: 100525, rate: 0.22 },
            { limit: Infinity, rate: 0.24 }
        ],
        'QW': [
            { limit: 23200, rate: 0.10 },
            { limit: 94300, rate: 0.12 },
            { limit: 201050, rate: 0.22 },
            { limit: Infinity, rate: 0.24 }
        ]
    };

    let remainingTaxableIncome = estimatedTaxableIncome;
    let currentTax = 0;
    const applicableBrackets = brackets[filingStatus] || brackets['SINGLE']; // Default to Single if status not found

    for (let i = 0; i < applicableBrackets.length; i++) {
        const bracket = applicableBrackets[i];
        const previousLimit = (i === 0) ? 0 : applicableBrackets[i - 1].limit;
        const taxableInBracket = Math.min(remainingTaxableIncome, bracket.limit - previousLimit);
        currentTax += taxableInBracket * bracket.rate;
        remainingTaxableIncome -= taxableInBracket;
        if (remainingTaxableIncome <= 0) break;
    }

    federalTaxBeforeCredits = currentTax;

    let estimatedFederalTaxLiability = Math.max(0, federalTaxBeforeCredits - totalFederalTaxCredits);

    // --- Estimated State Tax Liability (Simplified) ---
    // State tax laws vary significantly. For this tool, a simplified fixed percentage is used.
    // Users should consult state-specific calculators or tax professionals.
    const simplifiedStateTaxRate = 0.05; // 5% of federal taxable income
    const estimatedStateTaxLiability = estimatedTaxableIncome * simplifiedStateTaxRate;

    // --- Total Estimated Tax Liability ---
    const totalEstimatedTaxLiability = estimatedFederalTaxLiability + estimatedStateTaxLiability;

    // --- Potential Tax Savings (vs. Standard Deduction only, no credits baseline) ---
    let baselineTaxableIncome = Math.max(0, grossIncome - standardDeduction);
    let baselineFederalTax = 0;

    let remainingBaselineIncome = baselineTaxableIncome;
    for (let i = 0; i < applicableBrackets.length; i++) {
        const bracket = applicableBrackets[i];
        const previousLimit = (i === 0) ? 0 : applicableBrackets[i - 1].limit;
        const taxableInBracket = Math.min(remainingBaselineIncome, bracket.limit - previousLimit);
        baselineFederalTax += taxableInBracket * bracket.rate;
        remainingBaselineIncome -= taxableInBracket;
        if (remainingBaselineIncome <= 0) break;
    }
    const baselineStateTax = baselineTaxableIncome * simplifiedStateTaxRate;
    const baselineTotalTax = baselineFederalTax + baselineStateTax;

    const totalTaxSavings = Math.max(0, baselineTotalTax - totalEstimatedTaxLiability);

    return {
        optimalDeductionAmount: optimalDeductionAmount,
        estimatedTaxableIncome: estimatedTaxableIncome,
        totalFederalTaxCredits: totalFederalTaxCredits,
        estimatedFederalTaxLiability: estimatedFederalTaxLiability,
        estimatedStateTaxLiability: estimatedStateTaxLiability,
        totalEstimatedTaxLiability: totalEstimatedTaxLiability,
        totalTaxSavings: totalTaxSavings
    };
}
