function calculate({
  policyDurationYears,
  baselineEmissionsAnnual,
  energyMixShiftRenewablePercentage,
  energyMixShiftFossilFuelPercentage,
  carbonIntensityReductionRate,
  carbonPricePerTon,
  inducedEconomicGrowthRate,
  emissionLeakageFactor
}) {
  // Input validation and default values for robustness
  policyDurationYears = parseFloat(policyDurationYears) || 0;
  baselineEmissionsAnnual = parseFloat(baselineEmissionsAnnual) || 0;
  energyMixShiftRenewablePercentage = parseFloat(energyMixShiftRenewablePercentage) || 0;
  energyMixShiftFossilFuelPercentage = parseFloat(energyMixShiftFossilFuelPercentage) || 0;
  carbonIntensityReductionRate = parseFloat(carbonIntensityReductionRate) || 0;
  carbonPricePerTon = parseFloat(carbonPricePerTon) || 0; // Can be negative for subsidies
  inducedEconomicGrowthRate = parseFloat(inducedEconomicGrowthRate) || 0; // Can be negative for contraction
  emissionLeakageFactor = parseFloat(emissionLeakageFactor) || 0;

  // Ensure non-negative percentages and duration
  if (policyDurationYears < 0) policyDurationYears = 0;
  if (baselineEmissionsAnnual < 0) baselineEmissionsAnnual = 0;
  if (energyMixShiftRenewablePercentage < 0) energyMixShiftRenewablePercentage = 0;
  if (energyMixShiftFossilFuelPercentage < 0) energyMixShiftFossilFuelPercentage = 0;
  if (carbonIntensityReductionRate < 0) carbonIntensityReductionRate = 0;
  if (emissionLeakageFactor < 0) emissionLeakageFactor = 0;
  if (emissionLeakageFactor > 100) emissionLeakageFactor = 100;

  let totalNetChange = 0;
  let currentAnnualEmissions = baselineEmissionsAnnual; // Starting point for year 1's calculations

  for (let year = 1; year <= policyDurationYears; year++) {
    let emissionsChangeThisYear = 0;

    // 1. Impact from Energy Mix Shift (applied to current emissions base)
    const netEnergyMixShiftEffect = (energyMixShiftFossilFuelPercentage - energyMixShiftRenewablePercentage) / 100;
    emissionsChangeThisYear += currentAnnualEmissions * netEnergyMixShiftEffect;

    // 2. Impact from Carbon Intensity Reduction (applied to current emissions base)
    emissionsChangeThisYear -= currentAnnualEmissions * (carbonIntensityReductionRate / 100);

    // 3. Impact from Induced Economic Growth (applied to current emissions base)
    emissionsChangeThisYear += currentAnnualEmissions * (inducedEconomicGrowthRate / 100);

    // 4. Impact from Carbon Price/Allowance
    // Heuristic: A positive carbon price incentivizes reduction, a negative price (subsidy) incentivizes increase.
    // We assume a linear effect: e.g., $100/ton leads to a 1% reduction from current emissions base.
    // Coefficient 10000 ensures $100 price is 1% (100/10000 = 0.01)
    // Negative sign makes positive carbonPricePerTon result in emission reduction.
    emissionsChangeThisYear += currentAnnualEmissions * (carbonPricePerTon / -10000);

    // 5. Apply Leakage Factor
    // Leakage reduces the *net effect* of the policy within the defined scope. 
    // If emissionsChangeThisYear is a reduction (negative), leakage makes it less negative.
    // If emissionsChangeThisYear is an increase (positive), leakage makes it less positive.
    // This is equivalent to saying only (100 - leakage)% of the change *occurs within the policy's intended scope*.
    const effectiveChangeWithinScope = emissionsChangeThisYear * (1 - emissionLeakageFactor / 100);

    totalNetChange += effectiveChangeWithinScope;
    currentAnnualEmissions += effectiveChangeWithinScope; // Update base for next year's compounding effects

    // Ensure annual emissions don't become unrealistically negative (e.g., if reductions exceed baseline)
    if (currentAnnualEmissions < 0) {
      currentAnnualEmissions = 0;
    }
  }

  const averageAnnualCarbonChange = policyDurationYears > 0 ? totalNetChange / policyDurationYears : 0;

  let carbonImpactCategory;
  const absTotalNetChange = Math.abs(totalNetChange);
  if (absTotalNetChange < 1000) { // Less than 1,000 tonnes CO2e
    carbonImpactCategory = "Minor or Neutral Emission Impact";
  } else if (totalNetChange < 0 && absTotalNetChange >= 1000 && absTotalNetChange < 50000) {
    carbonImpactCategory = "Moderate Emission Reduction";
  } else if (totalNetChange < 0 && absTotalNetChange >= 50000 && absTotalNetChange < 500000) {
    carbonImpactCategory = "Significant Emission Reduction";
  } else if (totalNetChange < 0 && absTotalNetChange >= 500000) {
    carbonImpactCategory = "Very Significant Emission Reduction";
  } else if (totalNetChange > 0 && absTotalNetChange >= 1000 && absTotalNetChange < 50000) {
    carbonImpactCategory = "Moderate Emission Increase";
  } else if (totalNetChange > 0 && absTotalNetChange >= 50000 && absTotalNetChange < 500000) {
    carbonImpactCategory = "Significant Emission Increase";
  } else if (totalNetChange > 0 && absTotalNetChange >= 500000) {
    carbonImpactCategory = "Very Significant Emission Increase";
  } else {
    carbonImpactCategory = "Minor or Neutral Emission Impact";
  }

  return {
    netCarbonEmissionChangeTotal: totalNetChange,
    averageAnnualCarbonChange: averageAnnualCarbonChange,
    carbonImpactCategory: carbonImpactCategory
  };
}
