const annualInvestmentReturnDecimal = annualInvestmentReturn / 100;
const annualInflationRateDecimal = annualInflationRate / 100;
const monthsToGoal = yearsToGoal * 12;

// Handle edge cases where goal is effectively met or trivial inputs
if (yearsToGoal <= 0) {
    return {
        inflationAdjustedGoal: futureGoalAmount,
        futureValueCurrentSavings: currentSavings,
        futureValueExistingContributions: currentMonthlyContribution * 0,
        requiredFutureValue: Math.max(0, futureGoalAmount - currentSavings),
        requiredMonthlySavings: Math.max(0, futureGoalAmount - currentSavings),
        totalEstimatedPrincipalContributed: Math.max(currentSavings, futureGoalAmount)
    };
}

// 1. Calculate future value of the goal adjusted for inflation
const inflationAdjustedGoal = futureGoalAmount * Math.pow(1 + annualInflationRateDecimal, yearsToGoal);

// 2. Calculate future value of current savings
const futureValueCurrentSavings = currentSavings * Math.pow(1 + annualInvestmentReturnDecimal, yearsToGoal);

// Calculate monthly investment rate
let monthlyInvestmentRate;
if (annualInvestmentReturnDecimal === 0) {
    monthlyInvestmentRate = 0;
} else {
    monthlyInvestmentRate = Math.pow(1 + annualInvestmentReturnDecimal, 1 / 12) - 1;
}

// 3. Calculate future value of existing monthly contributions (if any)
let futureValueExistingContributions = 0;
if (currentMonthlyContribution > 0) {
    if (monthlyInvestmentRate === 0) {
        futureValueExistingContributions = currentMonthlyContribution * monthsToGoal;
    } else {
        futureValueExistingContributions = currentMonthlyContribution * (Math.pow(1 + monthlyInvestmentRate, monthsToGoal) - 1) / monthlyInvestmentRate;
    }
}

// 4. Calculate the remaining future value needed
let requiredFutureValue = inflationAdjustedGoal - futureValueCurrentSavings - futureValueExistingContributions;

// Ensure requiredFutureValue doesn't go below zero if goal is already exceeded
requiredFutureValue = Math.max(0, requiredFutureValue);

// 5. Calculate the new required monthly savings
let requiredMonthlySavings = 0;
if (requiredFutureValue > 0) {
    if (monthlyInvestmentRate === 0) {
        // If no investment return, simple division over months
        if (monthsToGoal > 0) {
            requiredMonthlySavings = requiredFutureValue / monthsToGoal;
        } else {
            requiredMonthlySavings = requiredFutureValue; // Immediate lump sum if monthsToGoal is 0
        }
    } else {
        // Annuity payment formula: P = FV / [((1 + r)^n - 1) / r]
        const annuityFactor = (Math.pow(1 + monthlyInvestmentRate, monthsToGoal) - 1) / monthlyInvestmentRate;
        if (annuityFactor > 0) {
            requiredMonthlySavings = requiredFutureValue / annuityFactor;
        } else {
            // This implies monthsToGoal is effectively zero or annuity factor is negligible, requiring lump sum
            requiredMonthlySavings = requiredFutureValue;
        }
    }
}

// 6. Total Estimated Principal Contributed (informative, excluding investment gains)
// This includes current savings, existing contributions, and the newly calculated required contributions.
let totalEstimatedPrincipalContributed = currentSavings + (currentMonthlyContribution + requiredMonthlySavings) * monthsToGoal;

return {
    inflationAdjustedGoal: inflationAdjustedGoal,
    futureValueCurrentSavings: futureValueCurrentSavings,
    futureValueExistingContributions: futureValueExistingContributions,
    requiredFutureValue: requiredFutureValue,
    requiredMonthlySavings: requiredMonthlySavings,
    totalEstimatedPrincipalContributed: totalEstimatedPrincipalContributed
};
