const numYears = endYear - startYear;
let equivalentAmount;
let totalInflation;
let purchasingPowerLoss;

if (numYears === 0) {
    equivalentAmount = initialAmount;
    totalInflation = 0;
    purchasingPowerLoss = 0;
} else {
    const inflationRateDecimal = averageInflationRate / 100;

    // The inflation factor calculates the cumulative change in price level.
    // If numYears is positive, it's (1+r)^n, representing price increase.
    // If numYears is negative, it's (1+r)^(-|n|) = 1/(1+r)^|n|, representing price decrease when looking backward.
    // Math.pow handles negative exponents correctly, producing 1 / (base ^ |exponent|).
    const inflationFactor = Math.pow(1 + inflationRateDecimal, numYears);

    // equivalentAmount is the value needed in the endYear to have the same purchasing power
    // as initialAmount in the startYear. This means initialAmount / (1+r)^n.
    // If n > 0, equivalentAmount < initialAmount (loss of power).
    // If n < 0, equivalentAmount > initialAmount (gain of power when looking backward from initialAmount's 'endYear').
    equivalentAmount = initialAmount / inflationFactor;

    // totalInflation represents the overall percentage change in prices from start to end.
    // If numYears < 0, inflationFactor will be < 1, so totalInflation will be negative (deflation).
    totalInflation = (inflationFactor - 1) * 100;

    // purchasingPowerLoss is initialAmount - equivalentAmount.
    // If endYear > startYear, equivalentAmount < initialAmount, so loss is positive.
    // If endYear < startYear, equivalentAmount > initialAmount, so loss is negative (a gain).
    purchasingPowerLoss = initialAmount - equivalentAmount;
}

return { equivalentAmount, totalInflation, purchasingPowerLoss };
