const { initialConstructionCost, expectedLifespanYears, annualMaintenanceCostPercentage, majorMaintenanceIntervalYears, majorMaintenanceCostPercentage, annualOperationalEnergyCost, annualOperationalNonEnergyCost, decommissioningCostPercentage, discountRate, inflationRate } = inputs;

const nominalDiscountRate = discountRate / 100;
const nominalInflationRate = inflationRate / 100;

let totalLifecycleCostNominal = 0;
let npvInitialConstruction = 0;
let npvMaintenance = 0;
let npvOperations = 0;
let npvDecommissioning = 0;

// 1. Initial Construction Cost
npvInitialConstruction = initialConstructionCost;
totalLifecycleCostNominal += initialConstructionCost;

// Handle expectedLifespanYears = 0 as an edge case: only initial cost applies
if (expectedLifespanYears === 0) {
  return {
    totalLifecycleCostNominal: initialConstructionCost,
    totalLifecycleCostPV: initialConstructionCost,
    annualizedLifecycleCostPV: initialConstructionCost, // If lifespan is 0, the entire cost is 'annualized' at once.
    npvInitialConstruction: initialConstructionCost,
    npvMaintenance: 0,
    npvOperations: 0,
    npvDecommissioning: 0
  };
}

// Iterate through each year of the lifespan for future costs
for (let year = 1; year <= expectedLifespanYears; year++) {
  let yearCostsNominal = 0;

  // Annual Routine Maintenance Cost
  const annualMaintNominal = initialConstructionCost * (annualMaintenanceCostPercentage / 100);
  const annualMaintInflatedNominal = annualMaintNominal * Math.pow(1 + nominalInflationRate, year);
  yearCostsNominal += annualMaintInflatedNominal;
  npvMaintenance += annualMaintInflatedNominal / Math.pow(1 + nominalDiscountRate, year);

  // Major Maintenance Cost (periodic)
  if (majorMaintenanceIntervalYears > 0 && year % majorMaintenanceIntervalYears === 0) {
    const majorMaintNominal = initialConstructionCost * (majorMaintenanceCostPercentage / 100);
    const majorMaintInflatedNominal = majorMaintNominal * Math.pow(1 + nominalInflationRate, year);
    yearCostsNominal += majorMaintInflatedNominal;
    npvMaintenance += majorMaintInflatedNominal / Math.pow(1 + nominalDiscountRate, year);
  }

  // Annual Operational Costs (Energy + Non-Energy)
  const annualOpNominal = annualOperationalEnergyCost + annualOperationalNonEnergyCost;
  const annualOpInflatedNominal = annualOpNominal * Math.pow(1 + nominalInflationRate, year);
  yearCostsNominal += annualOpInflatedNominal;
  npvOperations += annualOpInflatedNominal / Math.pow(1 + nominalDiscountRate, year);

  totalLifecycleCostNominal += yearCostsNominal;
}

// Decommissioning Cost (at the end of lifespan)
const decommissioningNominal = initialConstructionCost * (decommissioningCostPercentage / 100);
const decommissioningInflatedNominal = decommissioningNominal * Math.pow(1 + nominalInflationRate, expectedLifespanYears);
totalLifecycleCostNominal += decommissioningInflatedNominal;
npvDecommissioning = decommissioningInflatedNominal / Math.pow(1 + nominalDiscountRate, expectedLifespanYears);

// Total Lifecycle Cost (Present Value)
const totalLifecycleCostPV = npvInitialConstruction + npvMaintenance + npvOperations + npvDecommissioning;

// Annualized Lifecycle Cost (Present Value)
let annualizedLifecycleCostPV = 0;
if (expectedLifespanYears > 0) {
  if (nominalDiscountRate > 0) {
    // Capital Recovery Factor formula: A = PV * [i(1 + i)^n] / [(1 + i)^n – 1]
    annualizedLifecycleCostPV = totalLifecycleCostPV * (nominalDiscountRate * Math.pow(1 + nominalDiscountRate, expectedLifespanYears)) / (Math.pow(1 + nominalDiscountRate, expectedLifespanYears) - 1);
  } else {
    // If discount rate is 0, simply divide total PV by lifespan
    annualizedLifecycleCostPV = totalLifecycleCostPV / expectedLifespanYears;
  }
} else {
  // If lifespan is 0, annualized cost is the total initial cost (or 0 if totalLifecycleCostPV is 0)
  annualizedLifecycleCostPV = totalLifecycleCostPV; // This reflects the entire cost occurring at once.
}

return {
  totalLifecycleCostNominal,
  totalLifecycleCostPV,
  annualizedLifecycleCostPV,
  npvInitialConstruction,
  npvMaintenance,
  npvOperations,
  npvDecommissioning
};
