function calculate({
    initialProjectCost, resilienceInvestment, projectLifespanYears, annualExtremeEventProbability,
    avgDamageWithoutResilience, avgDamageWithResilience, avgOperationalLossPerEvent, avgOperationalLossReduced
}) {
    // Input validation and sanitization
    initialProjectCost = Math.max(0, initialProjectCost || 0);
    resilienceInvestment = Math.max(0, resilienceInvestment || 0);
    projectLifespanYears = Math.max(1, projectLifespanYears || 1); // Min 1 year lifespan
    annualExtremeEventProbability = Math.max(0, Math.min(100, annualExtremeEventProbability || 0)) / 100; // Cap 0-100%
    avgDamageWithoutResilience = Math.max(0, avgDamageWithoutResilience || 0);
    avgDamageWithResilience = Math.max(0, avgDamageWithResilience || 0);
    avgOperationalLossPerEvent = Math.max(0, avgOperationalLossPerEvent || 0);
    avgOperationalLossReduced = Math.max(0, Math.min(100, avgOperationalLossReduced || 0)) / 100; // Cap 0-100%

    // Step 1: Calculate Total Resilience Investment
    const totalResilienceCost = resilienceInvestment;

    // Step 2: Calculate Expected Damage Avoided Over Project Lifespan
    const expectedAnnualDamageWithoutResilience = annualExtremeEventProbability * avgDamageWithoutResilience;
    const expectedAnnualDamageWithResilience = annualExtremeEventProbability * avgDamageWithResilience;
    const annualDamageAvoided = Math.max(0, expectedAnnualDamageWithoutResilience - expectedAnnualDamageWithResilience); // Ensure non-negative
    const totalExpectedAvoidedDamage = annualDamageAvoided * projectLifespanYears;

    // Step 3: Calculate Expected Operational Continuity Benefit Over Project Lifespan
    const expectedAnnualOperationalLossWithoutResilience = annualExtremeEventProbability * avgOperationalLossPerEvent;
    const expectedAnnualOperationalLossWithResilience = expectedAnnualOperationalLossWithoutResilience * (1 - avgOperationalLossReduced);
    const annualOperationalContinuityBenefit = Math.max(0, expectedAnnualOperationalLossWithoutResilience - expectedAnnualOperationalLossWithResilience); // Ensure non-negative
    const totalExpectedOperationalBenefit = annualOperationalContinuityBenefit * projectLifespanYears;

    // Step 4: Calculate Total Benefits
    const totalBenefits = totalExpectedAvoidedDamage + totalExpectedOperationalBenefit;

    // Step 5: Calculate Cost-Benefit Ratio and Net Benefit
    let costBenefitRatio = 0;
    if (totalResilienceCost > 0) {
        costBenefitRatio = totalBenefits / totalResilienceCost;
    } else if (totalBenefits > 0) {
        costBenefitRatio = Infinity; // Infinite return if no cost but benefit exists
    } // else, if totalResilienceCost is 0 and totalBenefits is 0 or negative, CBR remains 0.

    const netBenefit = totalBenefits - totalResilienceCost;

    return {
        totalResilienceCost,
        totalExpectedAvoidedDamage,
        totalExpectedOperationalBenefit,
        costBenefitRatio,
        netBenefit
    };
}
