const upfrontCost = inputs.upfrontCost;
const infrastructureLifespan = inputs.infrastructureLifespan;
const annualDamageProbability = inputs.annualDamageProbability / 100;
const futureEventFrequencyIncrease = inputs.futureEventFrequencyIncrease;
const avgDamageCost = inputs.avgDamageCost;
const avgDisruptionCost = inputs.avgDisruptionCost;
const discountRate = inputs.discountRate / 100;

let totalAvoidedCosts = 0;

// Calculate the effective annual probability of a major event, factoring in future increases, capped at 100%
const effectiveAnnualProbability = Math.min(annualDamageProbability * futureEventFrequencyIncrease, 1);

// Iterate through the lifespan to calculate and discount avoided costs for each year
for (let year = 1; year <= infrastructureLifespan; year++) {
    const avoidedDamageAndDisruptionThisYear = effectiveAnnualProbability * (avgDamageCost + avgDisruptionCost);
    // Discount future avoided costs to present value
    const discountedAvoidedCostsThisYear = avoidedDamageAndDisruptionThisYear / Math.pow(1 + discountRate, year);
    totalAvoidedCosts += discountedAvoidedCostsThisYear;
}

const netBenefit = totalAvoidedCosts - upfrontCost;

// Handle cases where upfrontCost is zero to prevent division by zero for BCR
const benefitCostRatio = upfrontCost > 0 ? totalAvoidedCosts / upfrontCost : Infinity;

return {
    totalAvoidedCosts: totalAvoidedCosts,
    netBenefit: netBenefit,
    benefitCostRatio: benefitCostRatio,
};
