function calculate({
  snowDepth,
  snowDensityType,
  infrastructureType,
  roofPitch,
  designLiveLoadCapacity,
  structuralConditionFactor,
  desiredSafetyMargin
}) {
  // Input Validation and Defaults
  snowDepth = parseFloat(snowDepth) || 0;
  roofPitch = parseFloat(roofPitch) || 0;
  designLiveLoadCapacity = parseFloat(designLiveLoadCapacity) || 0;
  structuralConditionFactor = parseFloat(structuralConditionFactor) || 1.0;
  desiredSafetyMargin = parseFloat(desiredSafetyMargin) || 1.5;

  if (snowDepth < 0) return { error: "Snow depth cannot be negative." };
  if (designLiveLoadCapacity <= 0) return { error: "Design Live Load Capacity must be positive." };
  if (structuralConditionFactor < 0.5 || structuralConditionFactor > 1.5) return { error: "Structural Condition Factor must be between 0.5 and 1.5." };
  if (desiredSafetyMargin < 1.0) return { error: "Desired Safety Margin must be 1.0 or greater." };

  // 1. Determine Snow Density (lbs/ft^3)
  let snowDensity_lb_ft3 = 15; // Default to medium snow
  const densityTypeLower = String(snowDensityType).toLowerCase().trim();
  if (densityTypeLower.includes('light') || densityTypeLower.includes('fluffy')) {
    snowDensity_lb_ft3 = 8; // approx 5-10 lbs/ft^3
  } else if (densityTypeLower.includes('heavy') || densityTypeLower.includes('wet')) {
    snowDensity_lb_ft3 = 35; // approx 20-50 lbs/ft^3
  } else if (densityTypeLower.includes('medium')) {
    snowDensity_lb_ft3 = 15; // approx 10-20 lbs/ft^3
  }
  // Allow direct numerical input for density if parsed
  const customDensity = parseFloat(snowDensityType);
  if (!isNaN(customDensity) && customDensity > 0) {
    snowDensity_lb_ft3 = customDensity;
  }

  // 2. Calculate Ground Snow Load
  const snowDepth_ft = snowDepth / 12; // Convert inches to feet
  const calculatedGroundSnowLoad = snowDepth_ft * snowDensity_lb_ft3;

  // 3. Adjust for Structure Type and Pitch (Simplistic factors, real codes are complex)
  let structureLoadFactor = 0.7; // Exposure factor (Ce) - typical for roofs, reduces ground snow load
  let thermalFactor = 1.0; // Ct - generally 1.0 for unheated structures, 1.0-1.2 for heated (melt/freeze)
  let pitchFactor = 1.0; // Cr - Roof pitch factor
  let importanceFactor = 1.0; // Is - Factor for essential facilities, not used here for simplicity but conceptually relevant

  const infraTypeLower = String(infrastructureType).toLowerCase().trim();

  if (infraTypeLower.includes('roof')) {
    if (roofPitch > 0) {
      if (roofPitch <= 30) {
        pitchFactor = 1.0; // No reduction for shallow pitches
      } else if (roofPitch > 70) {
        pitchFactor = 0.0; // Significant reduction for steep pitches (snow slides off)
      } else {
        // Linear reduction between 30 and 70 degrees
        pitchFactor = 1 - ((roofPitch - 30) / 40);
      }
    } else {
      // Flat roof: potential for drifts, but basic factor remains
      pitchFactor = 1.0;
    }
    // For roofs, exposure and thermal factors typically apply
    structureLoadFactor = 0.7; // Typical Ce for sheltered/partially exposed, can be 0.9 for exposed, 1.2 for windward drifts
    thermalFactor = 1.0; // Assuming unheated or typical melting conditions
  } else if (infraTypeLower.includes('bridge') || infraTypeLower.includes('deck')) {
    // Bridges are generally considered ground snow load or slight variations, less pitch factor
    structureLoadFactor = 0.8; // Slightly reduced for wind scour
    thermalFactor = 1.0;
    pitchFactor = 1.0;
  } else if (infraTypeLower.includes('power line') || infraTypeLower.includes('tower')) {
    // Power lines and towers can accumulate snow/ice, often different design criteria
    // For simplicity, we'll use a slightly higher factor due to adherence/icing potential
    structureLoadFactor = 1.0; // Closer to ground snow for structural elements accumulating snow
    thermalFactor = 1.0;
    pitchFactor = 1.0;
  } else {
    // Default for other structures
    structureLoadFactor = 0.8;
    thermalFactor = 1.0;
    pitchFactor = 1.0;
  }

  const calculatedStructureSnowLoad = calculatedGroundSnowLoad * structureLoadFactor * pitchFactor * thermalFactor;

  // 4. Calculate Adjusted Design Capacity
  // The actual load capacity the structure can safely handle, factoring in its condition and desired buffer
  const adjustedDesignCapacity = (designLiveLoadCapacity / desiredSafetyMargin) * structuralConditionFactor;

  // 5. Calculate Stress Ratio
  let stressRatio = (calculatedStructureSnowLoad / adjustedDesignCapacity) * 100;
  stressRatio = Math.min(stressRatio, 1000); // Cap stress ratio at a very high value for display

  // 6. Determine Risk Level and Recommendation
  let riskLevel;
  let actionRecommendation;

  if (stressRatio <= 70) {
    riskLevel = "Low Risk";
    actionRecommendation = "Monitor conditions. No immediate action recommended beyond standard maintenance.";
  } else if (stressRatio <= 90) {
    riskLevel = "Moderate Risk";
    actionRecommendation = "Increased vigilance. Plan for snow removal if conditions worsen or forecast predicts more accumulation. Conduct preliminary inspection.";
  } else if (stressRatio <= 110) {
    riskLevel = "High Risk";
    actionRecommendation = "Immediate structural inspection by qualified personnel. Consider emergency snow removal to reduce load. Restrict access if necessary.";
  } else {
    riskLevel = "Extreme Risk";
    actionRecommendation = "Urgent emergency snow removal. Evacuate or restrict access to the structure immediately. Contact structural engineering experts for urgent assessment and intervention.";
  }

  return {
    calculatedGroundSnowLoad: calculatedGroundSnowLoad,
    calculatedStructureSnowLoad: calculatedStructureSnowLoad,
    stressRatio: stressRatio,
    riskLevel: riskLevel,
    actionRecommendation: actionRecommendation
  };
}
