const numRecordsActual = inputs.numSensitiveRecords * 1000000;

const dataBreachRisk = numRecordsActual * inputs.avgDataBreachCostPerRecord * (inputs.probInsiderDataExfiltration / 100);

const operationalDisruptionRisk = inputs.costOpDisruptionPerHour * inputs.avgDurationInsiderDisruptionHours * (inputs.probInsiderOpDisruption / 100);

const annualFinancialRiskWithoutMitigation = dataBreachRisk + operationalDisruptionRisk;

const riskReductionDueToMitigation = annualFinancialRiskWithoutMitigation * (inputs.mitigationEffectiveness / 100);

const annualFinancialRiskWithMitigation = annualFinancialRiskWithoutMitigation - riskReductionDueToMitigation;

const annualNetSavingsFromMitigation = riskReductionDueToMitigation - inputs.annualMitigationCost;

let roi = 0;
if (inputs.annualMitigationCost > 0) {
    roi = (annualNetSavingsFromMitigation / inputs.annualMitigationCost) * 100;
} else if (riskReductionDueToMitigation > 0) {
    // If cost is zero but there's a benefit, ROI is effectively infinite. Represent with a very high number.
    roi = 9999999999;
} else {
    roi = 0; // No cost, no benefit or negative benefit.
}

return {
    annualFinancialRiskWithoutMitigation,
    annualFinancialRiskWithMitigation,
    annualSavingsFromMitigation: annualNetSavingsFromMitigation,
    roi
};
