function calculate(inputs) {
  const { 
    controversySeverity, 
    mediaExposureScore, 
    stakeholderTrustImpactScore, 
    priorReputationScore, 
    responseTimeHours, 
    institutionalBudgetMillions, 
    potentialDonorLossPercentage, 
    potentialEnrollmentDeclinePercentage,
    legalSettlementEstimateMillions
  } = inputs;

  // --- Calculate Reputational Damage Score (0-100) ---
  let controversyImpact = controversySeverity * 3.5;
  let mediaImpact = mediaExposureScore * 3;
  let stakeholderImpact = stakeholderTrustImpactScore * 2.5;

  // Prior reputation reduces the damage
  let priorReputationReduction = priorReputationScore * 2; // Max 20 reduction for excellent rep

  // Response time penalty/bonus
  let responseTimePenalty = 0;
  if (responseTimeHours > 72) {
    responseTimePenalty = 15; // Very slow response
  } else if (responseTimeHours > 48) {
    responseTimePenalty = 10; // Slow response
  } else if (responseTimeHours > 24) {
    responseTimePenalty = 5; // Moderate delay
  } else if (responseTimeHours < 6) {
    responseTimePenalty = -5; // Quick, proactive response bonus
  }

  let rawReputationalDamage = controversyImpact + mediaImpact + stakeholderImpact - priorReputationReduction + responseTimePenalty;
  const reputationalDamageScore = Math.max(0, Math.min(100, rawReputationalDamage));

  // --- Calculate Estimated Financial Impact ---
  const directDonorLoss = institutionalBudgetMillions * (potentialDonorLossPercentage / 100);

  // Enrollment decline typically doesn't impact 1:1 with budget, estimating 50% revenue impact per percentage point.
  const directEnrollmentLoss = institutionalBudgetMillions * (potentialEnrollmentDeclinePercentage / 100) * 0.5;

  // Indirect costs due to reputation damage (e.g., increased marketing spend, deferred projects, reduced future opportunities)
  // This is a percentage of the budget scaled by the reputational damage score.
  const indirectReputationCost = institutionalBudgetMillions * (reputationalDamageScore / 100) * 0.1; // Up to 10% of budget

  // Total financial impact
  let estimatedFinancialImpactMillions = directDonorLoss + directEnrollmentLoss + indirectReputationCost + legalSettlementEstimateMillions;

  // Ensure financial impact is not negative
  estimatedFinancialImpactMillions = Math.max(0, estimatedFinancialImpactMillions);

  return {
    reputationalDamageScore: Math.round(reputationalDamageScore),
    estimatedFinancialImpactMillions: estimatedFinancialImpactMillions
  };
}
