formula:
const totalRValueInsulationLayer = inputs.insulation_thickness_in * inputs.r_value_insulation;

let total_r_value = totalRValueInsulationLayer + inputs.r_value_drywall + inputs.r_value_exterior_sheathing + inputs.r_value_exterior_finish + inputs.r_value_air_film_interior + inputs.r_value_air_film_exterior;

// Ensure total R-value is not zero or negative to avoid division by zero for U-factor
if (total_r_value <= 0) {
    total_r_value = 0.001; // Cap to a very small positive value
}

const u_factor = 1 / total_r_value;

let ua_heat_loss = u_factor * inputs.wall_area_sf;

// Ensure heat loss is non-negative if area is zero or negative
if (inputs.wall_area_sf <= 0) {
    ua_heat_loss = 0;
}

return {
    total_r_value: total_r_value,
    u_factor: u_factor,
    ua_heat_loss: ua_heat_loss
};
