function calculate({ initialInvestment, annualGrossRevenue, annualOperatingCosts, projectDurationYears, baseDiscountRate, geopoliticalRiskScore, regulatoryEnvironmentRiskScore, resourceCertaintyRiskScore, environmentalComplianceCostFactor }) {

  // Input validation and caps
  initialInvestment = Math.max(0, initialInvestment || 0);
  annualGrossRevenue = Math.max(0, annualGrossRevenue || 0);
  annualOperatingCosts = Math.max(0, annualOperatingCosts || 0);
  projectDurationYears = Math.max(1, projectDurationYears || 1);
  baseDiscountRate = Math.max(0, (baseDiscountRate || 0) / 100); // Convert to decimal

  geopoliticalRiskScore = Math.min(Math.max(0, geopoliticalRiskScore || 0), 10);
  regulatoryEnvironmentRiskScore = Math.min(Math.max(0, regulatoryEnvironmentRiskScore || 0), 10);
  resourceCertaintyRiskScore = Math.min(Math.max(0, resourceCertaintyRiskScore || 0), 10);
  environmentalComplianceCostFactor = Math.min(Math.max(1.0, environmentalComplianceCostFactor || 1.0), 2.0);

  if (initialInvestment === 0 && annualGrossRevenue === 0 && annualOperatingCosts === 0) {
    return { netPresentValue: 0, riskAdjustedROI: 0, totalRiskAdjustmentFactor: baseDiscountRate * 100 };
  }

  // Calculate adjusted annual operating costs
  const adjustedAnnualOperatingCosts = annualOperatingCosts * environmentalComplianceCostFactor;

  // Calculate annual net cash flow
  const annualNetCashFlow = annualGrossRevenue - adjustedAnnualOperatingCosts;

  // Calculate composite risk factor
  const normalizedGeopoliticalRisk = geopoliticalRiskScore / 10;
  const normalizedRegulatoryRisk = regulatoryEnvironmentRiskScore / 10;
  const normalizedResourceRisk = resourceCertaintyRiskScore / 10;

  const averageNormalizedRisk = (normalizedGeopoliticalRisk + normalizedRegulatoryRisk + normalizedResourceRisk) / 3;

  // Define a maximum risk premium to add to the base discount rate (e.g., up to 15%)
  const maxRiskPremium = 0.15; 
  const riskPremium = averageNormalizedRisk * maxRiskPremium;

  // Calculate risk-adjusted discount rate
  const riskAdjustedDiscountRate = baseDiscountRate + riskPremium;
  const discountFactor = 1 + riskAdjustedDiscountRate;

  // Calculate Net Present Value (NPV)
  let npvCashFlows = 0;
  for (let t = 1; t <= projectDurationYears; t++) {
    npvCashFlows += annualNetCashFlow / Math.pow(discountFactor, t);
  }
  const netPresentValue = npvCashFlows - initialInvestment;

  // Calculate Risk-Adjusted ROI (as a percentage of NPV relative to initial investment)
  let riskAdjustedROI = 0;
  if (initialInvestment > 0) {
    riskAdjustedROI = (netPresentValue / initialInvestment) * 100;
  }

  return {
    netPresentValue: netPresentValue,
    riskAdjustedROI: riskAdjustedROI,
    totalRiskAdjustmentFactor: riskAdjustedDiscountRate * 100 
  };
}
