function calculateNPV(rate, cashFlows) {
    let npv = 0;
    for (let i = 0; i < cashFlows.length; i++) {
        if (i === 0) {
            npv += cashFlows[i];
        } else {
            npv += cashFlows[i] / Math.pow(1 + rate, i);
        }
    }
    return npv;
}

const initialInvestment = parseFloat(inputs.initialInvestment);
const cf1 = parseFloat(inputs.cashFlowYear1);
const cf2 = parseFloat(inputs.cashFlowYear2 || 0);
const cf3 = parseFloat(inputs.cashFlowYear3 || 0);
const cf4 = parseFloat(inputs.cashFlowYear4 || 0);
const cf5 = parseFloat(inputs.cashFlowYear5 || 0);

const cashFlows = [-initialInvestment, cf1, cf2, cf3, cf4, cf5].filter(cf => !isNaN(cf) && cf !== null);

// Input validation
if (initialInvestment <= 0) {
    return { irrPercentage: 'Error', paybackPeriodApprox: 'Initial investment must be positive.' };
}

let hasPositiveCashFlow = cashFlows.slice(1).some(cf => cf > 0);
if (!hasPositiveCashFlow) {
    return { irrPercentage: '-100.00', paybackPeriodApprox: 'Never (no positive inflows)' };
}

const MAX_ITERATIONS = 5000; // Increased iterations for robustness
const ACCURACY = 0.000001; // 0.0001%

let lowRate = -0.999; // Represents -99.9% (-100% is undefined for (1+r)) 
let highRate = 100.0; // Represents 10,000% (arbitrarily high positive bound)
let irr = 0;

// Bisection method to find IRR
for (let i = 0; i < MAX_ITERATIONS; i++) {
    irr = (lowRate + highRate) / 2;
    const npv = calculateNPV(irr, cashFlows);

    if (Math.abs(npv) < ACCURACY) {
        break;
    }

    if (npv > 0) {
        lowRate = irr; 
    } else {
        highRate = irr; 
    }

    // If range becomes too small without convergence and NPV is still not zero, it might be flat or no real root.
    if (Math.abs(highRate - lowRate) < ACCURACY && Math.abs(npv) > ACCURACY) {
        break;
    }
}

// Final check for convergence and edge cases
const finalNPV = calculateNPV(irr, cashFlows);
let irrOutput;

if (Math.abs(finalNPV) > 0.01 && Math.abs(highRate - lowRate) > ACCURACY * 10) { // If not converged properly
    if (calculateNPV(highRate, cashFlows) > 0) {
        irrOutput = 'IRR is extremely high (>10000%)';
    } else if (calculateNPV(lowRate, cashFlows) < 0) {
        irrOutput = 'IRR is extremely low (<-99%)';
    } else {
        irrOutput = 'Could not converge to a valid IRR (multiple or no real roots likely).';
    }
} else if (irr === highRate && calculateNPV(highRate, cashFlows) < 0) { // If it converged to a high rate, but NPV is still negative
    irrOutput = 'IRR is extremely low (<-99%)'; // Correct logic here
} else if (irr === lowRate && calculateNPV(lowRate, cashFlows) > 0) { // If it converged to a low rate, but NPV is still positive
    irrOutput = 'IRR is extremely high (>10000%)'; // Correct logic here
} else {
    irrOutput = (irr * 100).toFixed(2);
}

// Payback period calculation
let cumulativeCashFlowForPP = -initialInvestment;
let paybackPeriodValue = 'Never (not recovered)';
let yearsCount = cashFlows.slice(1).length;

for (let i = 0; i < yearsCount; i++) {
    const cashInflowThisYear = cashFlows[i + 1];
    if (cumulativeCashFlowForPP < 0) { // Still needs to recover
        if (cashInflowThisYear > 0 && (cumulativeCashFlowForPP + cashInflowThisYear) >= 0) {
            const amountStillNeeded = -cumulativeCashFlowForPP;
            paybackPeriodValue = (i + (amountStillNeeded / cashInflowThisYear)).toFixed(2);
            break;
        }
    }
    cumulativeCashFlowForPP += cashInflowThisYear;
}

// If after the loop, the cumulative cash flow is positive but a specific period wasn't set, it means recovery happened by the end of the last year.
if (paybackPeriodValue === 'Never (not recovered)' && cumulativeCashFlowForPP >= 0) {
    paybackPeriodValue = yearsCount.toFixed(2);
}

return {
    irrPercentage: irrOutput,
    paybackPeriodApprox: paybackPeriodValue
};
