const annualGrossReduction = inputs.projectedEmissionsReduction;
const projectCostInitial = inputs.projectCost;
const lifespan = inputs.projectLifespan;
const additionality = inputs.additionalityFactor;
const leakage = inputs.leakageFactor;
const permanenceRisk = inputs.permanenceRiskFactor;
const mvCostPerTon = inputs.monitoringVerificationCostPerTon;
const discountRate = inputs.discountRate / 100;

// Ensure factors are within valid range [0, 1]
const adjAdditionality = Math.min(1, Math.max(0, additionality));
const adjLeakage = Math.min(1, Math.max(0, leakage));
const adjPermanenceRisk = Math.min(1, Math.max(0, permanenceRisk));

// Step 1: Calculate Effective Annual Reduction
const effectiveAnnualReduction = annualGrossReduction * adjAdditionality * (1 - adjLeakage) * (1 - adjPermanenceRisk);

// Handle edge case where effective reduction is zero or negative
if (effectiveAnnualReduction <= 0) {
    return {
        totalEffectiveReduction: 0,
        costPerEffectiveTon: Infinity,
        netPresentValueOffset: -projectCostInitial,
        overallEffectivenessScore: 0
    };
}

// Step 2: Calculate Total Effective Reduction over Lifespan
const totalEffectiveReduction = effectiveAnnualReduction * lifespan;

// Step 3: Calculate Total Project Cost (Initial + M&V)
const totalMVCostOverLifespan = annualGrossReduction * mvCostPerTon * lifespan;
const totalAdjustedProjectCost = projectCostInitial + totalMVCostOverLifespan;

// Step 4: Calculate Cost Per Effective Ton
const costPerEffectiveTon = totalAdjustedProjectCost / totalEffectiveReduction;

// Step 5: Calculate Net Present Value of Offsetting Benefits
const assumedCarbonCreditValue = 30; // A placeholder for a market-assumed value per ton for NPV calculation

let netPresentValueOffset = -projectCostInitial; // Start with the initial outlay

for (let i = 1; i <= lifespan; i++) {
    const discountFactor = 1 / Math.pow(1 + discountRate, i);
    const annualBenefit = effectiveAnnualReduction * assumedCarbonCreditValue;
    const annualMVCost = annualGrossReduction * mvCostPerTon; 
    netPresentValueOffset += (annualBenefit - annualMVCost) * discountFactor;
}

// Step 6: Calculate Overall Effectiveness Score (0-100)
const benchmarkLowCost = 10; // $/tCO2e - very good
const benchmarkHighCost = 100; // $/tCO2e - poor
const benchmarkHighReduction = 1000000; // tCO2e - very large project
const benchmarkLowReduction = 10000; // tCO2e - smaller project

let scoreCost = 0;
if (costPerEffectiveTon <= benchmarkLowCost) {
    scoreCost = 50;
} else if (costPerEffectiveTon >= benchmarkHighCost) {
    scoreCost = 0;
} else {
    scoreCost = 50 * (1 - (costPerEffectiveTon - benchmarkLowCost) / (benchmarkHighCost - benchmarkLowCost));
}

let scoreReduction = 0;
if (totalEffectiveReduction >= benchmarkHighReduction) {
    scoreReduction = 50;
} else if (totalEffectiveReduction <= benchmarkLowReduction) {
    scoreReduction = 0;
} else {
    scoreReduction = 50 * ((totalEffectiveReduction - benchmarkLowReduction) / (benchmarkHighReduction - benchmarkReduction));
}

let overallEffectivenessScore = scoreCost + scoreReduction;
overallEffectivenessScore = Math.min(100, Math.max(0, overallEffectivenessScore)); // Cap between 0 and 100

return {
    totalEffectiveReduction: parseFloat(totalEffectiveReduction.toFixed(0)),
    costPerEffectiveTon: parseFloat(costPerEffectiveTon.toFixed(2)),
    netPresentValueOffset: parseFloat(netPresentValueOffset.toFixed(2)),
    overallEffectivenessScore: parseFloat(overallEffectivenessScore.toFixed(1))
};
