formula:
/**
 * International Relocation Cost and Lifestyle Impact Calculator Formula
 * All monetary values are calculated in USD for simplicity and consistency, 
 * reflecting a common international reference currency. Users should mentally convert
 * or use an external tool for precise local currency equivalents.
 */

const originCity = inputs.originCity.toLowerCase();
const destinationCity = inputs.destinationCity.toLowerCase();
const numAdults = parseFloat(inputs.numAdults);
const numChildren = parseFloat(inputs.numChildren);
const visaType = inputs.visaType.toLowerCase();
const initialHousingDeposit = parseFloat(inputs.initialHousingDeposit);
const shippingLogisticsBudget = parseFloat(inputs.shippingLogisticsBudget);
const flightTravelBudget = parseFloat(inputs.flightTravelBudget);
const initialSettlingAllowance = parseFloat(inputs.initialSettlingAllowance);
const emergencyFundMonths = parseFloat(inputs.emergencyFundMonths);
const desiredLifestyle = inputs.desiredLifestyle.toLowerCase();

// --- Internal Data Lookup Tables (Simplified for demonstration) ---

// Base cost of living index (NYC = 100). Higher means more expensive.
// Monthly base living cost for 1 adult, 'comfortable' lifestyle, before lifestyle/child multipliers.
const costOfLivingBaseIndex = {
  'new york, usa': { index: 100, monthlyBaseCost: 4000 },
  'london, uk': { index: 90, monthlyBaseCost: 3800 },
  'berlin, germany': { index: 70, monthlyBaseCost: 2800 },
  'paris, france': { index: 95, monthlyBaseCost: 3900 },
  'sydney, australia': { index: 85, monthlyBaseCost: 3500 },
  'tokyo, japan': { index: 110, monthlyBaseCost: 4500 },
  'dubai, uae': { index: 80, monthlyBaseCost: 3300 },
  'singapore, singapore': { index: 105, monthlyBaseCost: 4300 },
  'toronto, canada': { index: 75, monthlyBaseCost: 3000 },
  'lisbon, portugal': { index: 60, monthlyBaseCost: 2400 },
  'mexico city, mexico': { index: 45, monthlyBaseCost: 1800 },
  'default': { index: 75, monthlyBaseCost: 3000 } // Fallback for unrecognized cities
};

// Estimated visa costs (per adult) in USD. These are rough estimates.
const visaCostData = {
  'skilled worker': 3000,
  'student': 1500,
  'family reunion': 2500,
  'digital nomad': 1000,
  'investor': 10000,
  'entrepreneur': 7000,
  'spouse': 2000,
  'tourist (long-term)': 500,
  'default': 1500 // Fallback for unrecognized visa types
};

// Multipliers for different lifestyle standards
const lifestyleMultipliers = {
  'modest': 0.75,
  'comfortable': 1.0,
  'luxury': 1.5
};

// Cost impact of children relative to an adult's base cost
const childCostFactor = 0.35; // Each child adds 35% of an adult's base cost

// --- Data Retrieval with Fallbacks ---

const originData = costOfLivingBaseIndex[originCity] || costOfLivingBaseIndex['default'];
const destinationData = costOfLivingBaseIndex[destinationCity] || costOfLivingBaseIndex['default'];
const visaBaseCost = visaCostData[visaType] || visaCostData['default'];
const lifestyleFactor = lifestyleMultipliers[desiredLifestyle] || lifestyleMultipliers['comfortable'];

// --- Calculations ---

// 1. Calculate Visa & Application Costs
const totalVisaCosts = visaBaseCost * numAdults;

// 2. Calculate Estimated Monthly Living Cost in Destination
let estimatedMonthlyLivingCost = destinationData.monthlyBaseCost;

// Adjust for multiple adults and children
estimatedMonthlyLivingCost *= (numAdults + (numChildren * childCostFactor));

// Adjust for desired lifestyle
estimatedMonthlyLivingCost *= lifestyleFactor;

// 3. Calculate Total Upfront Relocation Costs (excluding emergency fund)
const totalUpfrontRelocationCost = 
  totalVisaCosts +
  initialHousingDeposit +
  shippingLogisticsBudget +
  flightTravelBudget +
  initialSettlingAllowance;

// 4. Calculate Recommended Emergency Fund
const recommendedEmergencyFund = estimatedMonthlyLivingCost * emergencyFundMonths;

// 5. Calculate Total Initial Investment Required
const totalInitialInvestmentRequired = totalUpfrontRelocationCost + recommendedEmergencyFund;

// 6. Calculate Cost of Living Index Comparison (Destination vs. Origin)
let costOfLivingIndexComparison = (destinationData.index / originData.index) * 100;
// Cap comparison at reasonable ranges to avoid extreme outliers from default values
costOfLivingIndexComparison = Math.min(Math.max(costOfLivingIndexComparison, 50), 200);

// 7. Determine Lifestyle Impact Score (qualitative)
let lifestyleImpactScore = 'Moderate Adjustment Expected';
if (costOfLivingIndexComparison > 130) {
  lifestyleImpactScore = 'Significant Financial Adjustment Expected';
} else if (costOfLivingIndexComparison < 70) {
  lifestyleImpactScore = 'Potentially Positive Financial Impact / Lower Cost of Living';
} 

// Further refine based on desired lifestyle vs. destination cost
if (desiredLifestyle === 'luxury' && destinationData.index < 70) {
    lifestyleImpactScore = 'Luxury Lifestyle Potentially More Attainable';
} else if (desiredLifestyle === 'modest' && destinationData.index > 90) {
    lifestyleImpactScore = 'Significant Financial Discipline Required for Modest Lifestyle';
}

// Ensure all outputs are numbers and handle potential NaN from missing lookups
const safeNumber = (val) => isNaN(val) || !isFinite(val) ? 0 : val;

return {
  totalUpfrontRelocationCost: safeNumber(totalUpfrontRelocationCost),
  estimatedMonthlyLivingCost: safeNumber(estimatedMonthlyLivingCost),
  recommendedEmergencyFund: safeNumber(recommendedEmergencyFund),
  totalInitialInvestmentRequired: safeNumber(totalInitialInvestmentRequired),
  costOfLivingIndexComparison: safeNumber(costOfLivingIndexComparison),
  lifestyleImpactScore: lifestyleImpactScore,
  currencyNote: 'All monetary outputs are estimates in USD. Please consider current exchange rates for local currency conversion.'
};
