const COUNTRY_COST_PROFILES = {
    "canada": {
        visaBaseCost: 1500, 
        processingTimeMonths: 6,
        shippingPerCubicFoot: 10, 
        housingPerAdultPerMonth: 1200, 
        housingPerChildPerMonthAdd: 200, 
        livingPerAdultPerMonth: 1000, 
        livingPerChildPerMonth: 400, 
        bufferMonthsMin: 2
    },
    "germany": {
        visaBaseCost: 1000,
        processingTimeMonths: 4,
        shippingPerCubicFoot: 12,
        housingPerAdultPerMonth: 1000,
        housingPerChildPerMonthAdd: 150,
        livingPerAdultPerMonth: 900,
        livingPerChildPerMonth: 350,
        bufferMonthsMin: 2
    },
    "australia": {
        visaBaseCost: 2500,
        processingTimeMonths: 8,
        shippingPerCubicFoot: 15,
        housingPerAdultPerMonth: 1500,
        housingPerChildPerMonthAdd: 250,
        livingPerAdultPerMonth: 1200,
        livingPerChildPerMonth: 500,
        bufferMonthsMin: 3
    },
    "united kingdom": {
        visaBaseCost: 1800,
        processingTimeMonths: 5,
        shippingPerCubicFoot: 11,
        housingPerAdultPerMonth: 1400,
        housingPerChildPerMonthAdd: 220,
        livingPerAdultPerMonth: 1100,
        livingPerChildPerMonth: 450,
        bufferMonthsMin: 2
    },
    "united states": {
        visaBaseCost: 2000,
        processingTimeMonths: 7,
        shippingPerCubicFoot: 13,
        housingPerAdultPerMonth: 1800,
        housingPerChildPerMonthAdd: 300,
        livingPerAdultPerMonth: 1500,
        livingPerChildPerMonth: 600,
        bufferMonthsMin: 3
    },
    "new zealand": {
        visaBaseCost: 1700,
        processingTimeMonths: 6,
        shippingPerCubicFoot: 14,
        housingPerAdultPerMonth: 1300,
        housingPerChildPerMonthAdd: 200,
        livingPerAdultPerMonth: 1100,
        livingPerChildPerMonth: 450,
        bufferMonthsMin: 2
    },
    "ireland": {
        visaBaseCost: 1200,
        processingTimeMonths: 4,
        shippingPerCubicFoot: 10,
        housingPerAdultPerMonth: 1300,
        housingPerChildPerMonthAdd: 200,
        livingPerAdultPerMonth: 1000,
        livingPerChildPerMonth: 400,
        bufferMonthsMin: 2
    },
    "dubai, uae": {
        visaBaseCost: 1000,
        processingTimeMonths: 3,
        shippingPerCubicFoot: 10,
        housingPerAdultPerMonth: 1600,
        housingPerChildPerMonthAdd: 250,
        livingPerAdultPerMonth: 1300,
        livingPerChildPerMonth: 500,
        bufferMonthsMin: 2
    },
    "singapore": {
        visaBaseCost: 1500,
        processingTimeMonths: 4,
        shippingPerCubicFoot: 16,
        housingPerAdultPerMonth: 2000,
        housingPerChildPerMonthAdd: 350,
        livingPerAdultPerMonth: 1600,
        livingPerChildPerMonth: 700,
        bufferMonthsMin: 2
    },
    "default": {
        visaBaseCost: 1500,
        processingTimeMonths: 6,
        shippingPerCubicFoot: 12,
        housingPerAdultPerMonth: 1200,
        housingPerChildPerMonthAdd: 200,
        livingPerAdultPerMonth: 1000,
        livingPerChildPerMonth: 400,
        bufferMonthsMin: 2
    }
};

const VISA_TYPE_PROFILES = {
    "skilled worker": { feeMultiplier: 1.0, timeMultiplier: 1.0, additionalApplicantFactor: 0.7 },
    "family sponsorship": { feeMultiplier: 0.9, timeMultiplier: 1.2, additionalApplicantFactor: 0.5 },
    "student": { feeMultiplier: 0.7, timeMultiplier: 0.6, additionalApplicantFactor: 0.3 },
    "investor/entrepreneur": { feeMultiplier: 2.5, timeMultiplier: 1.5, additionalApplicantFactor: 0.8 },
    "visitor/tourist (extended)": { feeMultiplier: 0.3, timeMultiplier: 0.4, additionalApplicantFactor: 0.2 },
    "other": { feeMultiplier: 1.0, timeMultiplier: 1.0, additionalApplicantFactor: 0.7 }
};

const adults = Math.max(1, inputs.numberOfAdults || 1);
const children = Math.max(0, inputs.numberOfChildren || 0);

const destination = inputs.destinationCountry.toLowerCase().trim();
const visa = inputs.visaType.toLowerCase().trim();

const countryProfile = COUNTRY_COST_PROFILES[destination] || COUNTRY_COST_PROFILES["default"];
const visaProfile = VISA_TYPE_PROFILES[visa] || VISA_TYPE_PROFILES["other"];

// --- Costs Calculation ---

// Visa Fees
let totalVisaFees = countryProfile.visaBaseCost * visaProfile.feeMultiplier;
if (adults > 1) {
    totalVisaFees += countryProfile.visaBaseCost * (adults - 1) * visaProfile.additionalApplicantFactor * visaProfile.feeMultiplier;
}
// Children typically included under parent's visa or have minor fees, often a fraction of adult's dependent fee.
if (children > 0) {
    totalVisaFees += countryProfile.visaBaseCost * children * 0.15 * visaProfile.feeMultiplier; // Small fee for dependent children
}

// Shipping Cost
const belongingsVolume = Math.max(0, inputs.belongingsVolumeCubicFeet || 0);
const totalShippingCost = belongingsVolume * countryProfile.shippingPerCubicFoot;

// Initial Housing Cost
const initialHousingDuration = Math.max(1, inputs.initialHousingDurationMonths || 1);
const monthlyHousingCost = (countryProfile.housingPerAdultPerMonth * adults) + (countryProfile.housingPerChildPerMonthAdd * children);
const initialHousingCost = monthlyHousingCost * initialHousingDuration;

// Initial Living Expenses
const initialLivingExpenseDuration = Math.max(1, inputs.initialLivingExpenseMonths || 1);
const monthlyLivingCost = (countryProfile.livingPerAdultPerMonth * adults) + (countryProfile.livingPerChildPerMonth * children);
const initialLivingExpenses = monthlyLivingCost * initialLivingExpenseDuration;

// Total Estimated Cost
const totalEstimatedCost = totalVisaFees + totalShippingCost + initialHousingCost + initialLivingExpenses;

// --- Timeline Calculation ---

// Visa Processing Timeline
const visaProcessingTimeline = countryProfile.processingTimeMonths * visaProfile.timeMultiplier;

// Job Search Buffer
const jobSearchBuffer = Math.max(0, inputs.jobSearchDurationMonths || 0);

// General Relocation Buffer (for settling in, unforeseen delays)
const generalBuffer = countryProfile.bufferMonthsMin;

// Total Estimated Timeline
const estimatedTimelineMonths = visaProcessingTimeline + jobSearchBuffer + generalBuffer;

return {
    totalEstimatedCostUSD: totalEstimatedCost,
    estimatedTimelineMonths: estimatedTimelineMonths,
    visaFeesUSD: totalVisaFees,
    shippingCostUSD: totalShippingCost,
    initialHousingCostUSD: initialHousingCost,
    initialLivingExpensesUSD: initialLivingExpenses
};
