function calculateROI(inputs) {
  const initialCapitalCost = inputs.initialCapitalCost || 0;
  const annualEnergyProduction = inputs.annualEnergyProduction || 0;
  const averageElectricitySellingPrice = inputs.averageElectricitySellingPrice || 0;
  const projectLifespan = inputs.projectLifespan || 0;
  const annualOperatingMaintenanceCost = inputs.annualOperatingMaintenanceCost || 0;
  const localIncentiveGrant = inputs.localIncentiveGrant || 0;
  const gridConnectionInfrastructureCost = inputs.gridConnectionInfrastructureCost || 0;
  const geopoliticalRiskPremium = (inputs.geopoliticalRiskPremium || 0) / 100;
  const baseDiscountRate = (inputs.baseDiscountRate || 0) / 100;
  const electricityPriceEscalationRate = (inputs.electricityPriceEscalationRate || 0) / 100;
  const omCostEscalationRate = (inputs.omCostEscalationRate || 0) / 100;

  // Validate essential inputs
  if (projectLifespan <= 0 || annualEnergyProduction <= 0) {
    return {
      netPresentValue: 0,
      internalRateOfReturn: 0,
      paybackPeriod: 0,
      levelizedCostOfEnergy: 0
    };
  }

  // Adjusted discount rate factoring in geopolitical risk
  const adjustedDiscountRate = baseDiscountRate + geopoliticalRiskPremium;

  let cashFlows = [];
  let cumulativeCashFlow = 0;
  let paybackPeriod = 0;
  let totalEnergyProduced = 0;
  let totalDiscountedCosts = 0;
  let totalDiscountedEnergy = 0;

  // Year 0: Initial investment and upfront costs/grants
  const year0CashFlow = -initialCapitalCost - gridConnectionInfrastructureCost + localIncentiveGrant;
  cashFlows.push(year0CashFlow);
  cumulativeCashFlow = year0CashFlow;

  let currentElectricityPrice = averageElectricitySellingPrice;
  let currentOMCost = annualOperatingMaintenanceCost;

  for (let year = 1; year <= projectLifespan; year++) {
    currentElectricityPrice *= (1 + electricityPriceEscalationRate);
    currentOMCost *= (1 + omCostEscalationRate);

    const annualRevenue = annualEnergyProduction * currentElectricityPrice;
    const annualOperatingCashFlow = annualRevenue - currentOMCost;
    const discountedAnnualOperatingCashFlow = annualOperatingCashFlow / Math.pow(1 + adjustedDiscountRate, year);

    cashFlows.push(annualOperatingCashFlow); // For IRR calculation

    // For NPV
    cumulativeCashFlow += discountedAnnualOperatingCashFlow;

    // For Payback Period
    if (paybackPeriod === 0) {
      let undiscountedCumulativeCashFlow = 0;
      for (let i = 0; i <= year; i++) {
        undiscountedCumulativeCashFlow += cashFlows[i];
      }
      if (undiscountedCumulativeCashFlow >= 0) {
        paybackPeriod = year;
        // More precise payback if needed, but for simplicity, integer year is fine
        // if (year > 1) { // If it becomes positive during a year, not at the start
        //   paybackPeriod = year - 1 + (-cashFlows[year-1] / cashFlows[year]); // This is an approximation based on the previous year's negative value and current year's positive flow
        // } else {
        //    paybackPeriod = year; // If it pays back in the first year itself
        // }
      }
    }

    // For LCOE
    totalEnergyProduced += annualEnergyProduction;
    totalDiscountedCosts += (currentOMCost / Math.pow(1 + adjustedDiscountRate, year));
    totalDiscountedEnergy += (annualEnergyProduction / Math.pow(1 + adjustedDiscountRate, year));
  }

  const netPresentValue = cashFlows.reduce((acc, cf, i) => acc + (cf / Math.pow(1 + adjustedDiscountRate, i)), 0);

  // LCOE Calculation
  // Sum of discounted costs over total discounted energy produced
  const LCOE_numerator = initialCapitalCost + gridConnectionInfrastructureCost - localIncentiveGrant + totalDiscountedCosts;
  const LCOE_denominator = totalDiscountedEnergy;
  const levelizedCostOfEnergy = LCOE_denominator > 0 ? (LCOE_numerator / LCOE_denominator) : 0;

  // IRR Calculation (Newton-Raphson method or bisection for approximation)
  // A simplified approach for quick calculation within JSON context.
  // This is an iterative process. For a robust IRR, a library or more complex algorithm is typically used.
  // For this context, we will use a simplified bisection method.
  let lowRate = -0.99; // -99%
  let highRate = 5.00; // 500%
  const tolerance = 0.0001; // 0.01%
  const maxIterations = 1000;

  function npvForIRR(rate, cfs) {
    return cfs.reduce((acc, cf, i) => acc + (cf / Math.pow(1 + rate, i)), 0);
  }

  let internalRateOfReturn = 0;
  for (let i = 0; i < maxIterations; i++) {
    let midRate = (lowRate + highRate) / 2;
    if (midRate <= -1) { // Avoid division by zero or negative base in Math.pow
        midRate = -0.9999;
    }
    const npvMid = npvForIRR(midRate, cashFlows);

    if (Math.abs(npvMid) < tolerance) {
      internalRateOfReturn = midRate * 100;
      break;
    } else if (npvMid > 0) {
      lowRate = midRate;
    } else {
      highRate = midRate;
    }

    if (i === maxIterations -1) {
        // If max iterations reached without convergence, set to a known value or indicate failure
        internalRateOfReturn = (lowRate + highRate) / 2 * 100; // Best guess
    }
  }
  // Handle cases where IRR might be extremely high or low due to specific cash flow patterns
  if (internalRateOfReturn > 500 || internalRateOfReturn < -99) {
    internalRateOfReturn = 0; // Or a more suitable indicator of non-convergence/extreme value
  }

  return {
    netPresentValue: netPresentValue,
    internalRateOfReturn: internalRateOfReturn,
    paybackPeriod: paybackPeriod,
    levelizedCostOfEnergy: levelizedCostOfEnergy
  };
}
