function calculate() {
    const bondAmount = parseFloat(inputs.bondAmount);
    const bondDurationYears = parseFloat(inputs.bondDurationYears);
    const interestRateBond = parseFloat(inputs.interestRateBond) / 100; // Annual rate
    const inflationRate = parseFloat(inputs.inflationRate) / 100; // Annual rate
    const opportunityCostRate = parseFloat(inputs.opportunityCostRate) / 100; // Annual rate
    const legalFees = parseFloat(inputs.legalFees);
    const otherUpfrontCosts = parseFloat(inputs.otherUpfrontCosts);
    const bondRefundDelayMonths = parseFloat(inputs.bondRefundDelayMonths);

    // Input validation
    if (isNaN(bondAmount) || bondAmount < 0) return { error: "Bond amount must be a non-negative number." };
    if (isNaN(bondDurationYears) || bondDurationYears <= 0) return { error: "Bond duration must be a positive number." };
    if (isNaN(interestRateBond) || interestRateBond < 0) return { error: "Bond interest rate must be non-negative." };
    if (isNaN(inflationRate) || inflationRate < 0) return { error: "Inflation rate must be non-negative." };
    if (isNaN(opportunityCostRate) || opportunityCostRate < 0) return { error: "Opportunity cost rate must be non-negative." };
    if (isNaN(legalFees) || legalFees < 0) return { error: "Legal fees must be non-negative." };
    if (isNaN(otherUpfrontCosts) || otherUpfrontCosts < 0) return { error: "Other upfront costs must be non-negative." };
    if (isNaN(bondRefundDelayMonths) || bondRefundDelayMonths < 0) return { error: "Refund delay must be non-negative." };

    // Total time funds are locked (bond duration + refund delay for full financial impact)
    const totalTimeLockedYears = bondDurationYears + (bondRefundDelayMonths / 12);

    // --- Outputs Calculation ---

    // 1. Total Upfront Financial Outlay (Initial cash outflow including bond principal)
    const totalUpfrontCost = bondAmount + legalFees + otherUpfrontCosts;

    // 2. Nominal Interest Earned by the Bond (if any)
    // This is the nominal gain from the bond principal itself due to its interest rate.
    const nominalBondValueAtMaturity = bondAmount * Math.pow(1 + interestRateBond, bondDurationYears);
    const nominalBondInterestEarned = nominalBondValueAtMaturity - bondAmount;

    // 3. Total Opportunity Cost (What the bond amount could have earned elsewhere)
    // This is calculated over the entire period funds are locked.
    const futureValueAlternativeInvestment = bondAmount * Math.pow(1 + opportunityCostRate, totalTimeLockedYears);
    const totalOpportunityCost = futureValueAlternativeInvestment - bondAmount;

    // 4. Total Inflation Impact on the Bond Principal
    // Loss of purchasing power of the *original bond amount* by the time it's returned.
    const realValueOfInitialBondAmountAtReturn = bondAmount / Math.pow(1 + inflationRate, totalTimeLockedYears);
    const totalInflationImpact = bondAmount - realValueOfInitialBondAmountAtReturn;

    // 5. Net Bond Value at Maturity (Real value of what is returned)
    // This considers both nominal interest earned by the bond and the erosion by inflation.
    const netBondValueAtMaturity = nominalBondValueAtMaturity / Math.pow(1 + inflationRate, totalTimeLockedYears);

    // 6. Total Effective Financial Cost
    // This consolidates all negative financial impacts and subtracts positive ones.
    // It's the sum of:
    // - Upfront non-bond fees (legal fees, other costs)
    // - Total Opportunity Cost of the bond amount
    // - Total Inflation Impact on the principal (loss of purchasing power)
    // - MINUS any Nominal Interest Earned by the Bond (as this reduces the overall cost)
    const totalEffectiveCost = legalFees + otherUpfrontCosts + totalOpportunityCost + totalInflationImpact - nominalBondInterestEarned;

    // 7. Effective ROI of the Bond (Real return on the bond itself)
    // Compares the real value returned from the bond to the real value of the initial principal.
    let roiPercentage = 0;
    if (bondAmount > 0) {
        // Real value of the initial principal at the time of return
        const initialPrincipalRealValueConsideredAtReturn = bondAmount / Math.pow(1 + inflationRate, totalTimeLockedYears);
        if (initialPrincipalRealValueConsideredAtReturn === 0) { 
             roiPercentage = (netBondValueAtMaturity > 0) ? Infinity : 0; // Handle extreme inflation or zero initial real value
        } else {
            roiPercentage = ((netBondValueAtMaturity - initialPrincipalRealValueConsideredAtReturn) / initialPrincipalRealValueConsideredAtReturn) * 100;
        }
    }

    return {
        totalUpfrontCost: totalUpfrontCost,
        totalLostInterestOnBond: nominalBondInterestEarned,
        totalOpportunityCost: totalOpportunityCost,
        netBondValueAtMaturity: netBondValueAtMaturity,
        totalInflationImpact: totalInflationImpact,
        totalEffectiveCost: totalEffectiveCost,
        roiPercentage: roiPercentage,
    };
}
