/**
 * Calculates an international travel contingency budget based on various factors.
 * @param {object} inputs - The input values from the form.
 * @param {number} inputs.trip_duration_days - Duration of the trip in days.
 * @param {number} inputs.travelers_count - Number of people traveling.
 * @param {string} inputs.destination_risk_level - Risk level of the destination (low, medium, high, extreme).
 * @param {number} inputs.daily_expenses_per_person - Average daily spending per person (food, local transport, etc.).
 * @param {number} inputs.initial_flight_cost_per_person - Average one-way flight cost per person.
 * @param {number} inputs.initial_accommodation_cost_per_night - Average accommodation cost per night for the group.
 * @param {string} inputs.travel_insurance_level - Level of travel insurance coverage (none, basic, standard, premium).
 * @param {number} inputs.personal_buffer_percent - Additional personal buffer percentage.
 * @returns {object} An object containing the calculated contingency budget and its breakdowns.
 */
function calculateContingencyBudget(inputs) {
    const {
        trip_duration_days,
        travelers_count,
        destination_risk_level,
        daily_expenses_per_person,
        initial_flight_cost_per_person,
        initial_accommodation_cost_per_night,
        travel_insurance_level,
        personal_buffer_percent
    } = inputs;

    // Input validation and default values
    const duration = Math.max(1, trip_duration_days || 1);
    const travelers = Math.max(1, travelers_count || 1);
    const dailyExpenses = Math.max(0, daily_expenses_per_person || 0);
    const flightCostPerPerson = Math.max(0, initial_flight_cost_per_person || 0);
    const accommodationCostPerNight = Math.max(0, initial_accommodation_cost_per_night || 0);
    const personalBuffer = Math.min(50, Math.max(0, personal_buffer_percent || 0)) / 100;

    // Define Risk Multipliers for Destination
    const riskMultipliers = {
        low: {
            flight: 0.15, medical: 0.2, extendedStay: 0.1, misc: 0.05,
            extraDays: 1, baseMedicalCost: 3000
        },
        medium: {
            flight: 0.3, medical: 0.4, extendedStay: 0.2, misc: 0.15,
            extraDays: 2, baseMedicalCost: 7000
        },
        high: {
            flight: 0.5, medical: 0.7, extendedStay: 0.35, misc: 0.25,
            extraDays: 3, baseMedicalCost: 15000
        },
        extreme: {
            flight: 0.8, medical: 1.0, extendedStay: 0.5, misc: 0.4,
            extraDays: 4, baseMedicalCost: 30000
        }
    };
    const destRisk = riskMultipliers[destination_risk_level] || riskMultipliers.medium;

    // Define Insurance Mitigation Factors
    const insuranceMitigation = {
        none: { flight: 0, medical: 0, extendedStay: 0 },
        basic: { flight: 0.1, medical: 0.2, extendedStay: 0.1 },
        standard: { flight: 0.3, medical: 0.5, extendedStay: 0.3 },
        premium: { flight: 0.5, medical: 0.8, extendedStay: 0.5 }
    };
    const insurance = insuranceMitigation[travel_insurance_level] || insuranceMitigation.none;

    // Base Costs
    const totalInitialFlightCost = flightCostPerPerson * travelers;
    const totalDailyLivingCost = (dailyExpenses * travelers) + accommodationCostPerNight;
    const totalInitialTripCost = totalInitialFlightCost * 2 + (accommodationCostPerNight * duration) + (dailyExpenses * travelers * duration);

    let breakdown_flight_disruption = 0;
    let breakdown_medical_emergency = 0;
    let breakdown_extended_stay = 0;
    let breakdown_miscellaneous = 0;

    // 1. Flight Disruption Calculation
    // Potential cost is a percentage of total flight cost (round trip assumed)
    const potentialRebookingCost = totalInitialFlightCost * 2 * destRisk.flight;
    breakdown_flight_disruption = potentialRebookingCost * (1 - insurance.flight);
    // Ensure a minimum amount for flight disruption (e.g., 20% of one-way flight cost for minor changes)
    breakdown_flight_disruption = Math.max(breakdown_flight_disruption, totalInitialFlightCost * 0.2);
    // Cap flight disruption to 2.5x original flight cost in extreme cases (e.g., complete re-routing, multiple legs)
    breakdown_flight_disruption = Math.min(breakdown_flight_disruption, totalInitialFlightCost * 2.5);

    // 2. Medical Emergency Calculation
    // Base potential cost per traveler, adjusted by destination risk and number of travelers
    const baseMedicalPotential = destRisk.baseMedicalCost * travelers;
    breakdown_medical_emergency = baseMedicalPotential * (1 - insurance.medical);
    // Ensure a minimum for medical emergencies (e.g., $500 per person even with insurance)
    breakdown_medical_emergency = Math.max(breakdown_medical_emergency, travelers * 500);

    // 3. Extended Stay Calculation
    // Estimated extra days vary by risk, cost is total daily living cost
    const potentialExtendedStayCost = totalDailyLivingCost * destRisk.extraDays;
    breakdown_extended_stay = potentialExtendedStayCost * (1 - insurance.extendedStay);
    // Ensure a minimum of 1 day's living cost
    breakdown_extended_stay = Math.max(breakdown_extended_stay, totalDailyLivingCost);

    // 4. Miscellaneous Unforeseen Events Calculation
    // A percentage of total estimated trip cost (flights round trip, full accommodation, full daily expenses)
    // This component is generally not mitigated by standard travel insurance for truly miscellaneous items.
    breakdown_miscellaneous = totalInitialTripCost * destRisk.misc;
    // Ensure a minimum miscellaneous fund
    breakdown_miscellaneous = Math.max(breakdown_miscellaneous, travelers * 150);

    // Total Contingency Budget
    let subtotalBudget = breakdown_flight_disruption +
                         breakdown_medical_emergency +
                         breakdown_extended_stay +
                         breakdown_miscellaneous;

    let estimated_contingency_budget = subtotalBudget * (1 + personalBuffer);

    // Cap total budget at a reasonable upper limit for safety, e.g., 5 times the initial trip cost or $50,000, whichever is lower.
    estimated_contingency_budget = Math.min(estimated_contingency_budget, Math.max(50000, totalInitialTripCost * 5));
    // Ensure a minimum total budget (e.g., $500 per person for any international trip)
    estimated_contingency_budget = Math.max(estimated_contingency_budget, travelers * 500);

    // Recommended Emergency Cash Fund (e.g., 10-20% of total contingency budget, capped at $1500)
    let recommended_cash_fund = Math.round(Math.min(1500, estimated_contingency_budget * 0.15));
    recommended_cash_fund = Math.max(recommended_cash_fund, Math.min(500, estimated_contingency_budget * 0.05)); // Minimum cash of $50 or 5% of budget

    return {
        estimated_contingency_budget: Math.round(estimated_contingency_budget),
        breakdown_flight_disruption: Math.round(breakdown_flight_disruption),
        breakdown_medical_emergency: Math.round(breakdown_medical_emergency),
        breakdown_extended_stay: Math.round(breakdown_extended_stay),
        breakdown_miscellaneous: Math.round(breakdown_miscellaneous),
        recommended_cash_fund: Math.round(recommended_cash_fund)
    };
}
