function calculate({ currentPortfolioValue, targetEquityPercentage, currentEquityValue, targetBondPercentage, currentBondValue, targetOtherPercentage, currentOtherValue }) {
    if (currentPortfolioValue <= 0) {
        return {
            rebalanceAmountEquity: 0,
            rebalanceAmountBond: 0,
            rebalanceAmountOther: 0,
            currentEquityPercentageActual: 0,
            currentBondPercentageActual: 0,
            currentOtherPercentageActual: 0,
            error: "Current portfolio value must be greater than zero."
        };
    }

    const totalCurrentAssets = currentEquityValue + currentBondValue + currentOtherValue;

    // Normalize target percentages to sum to 100%
    const totalTargetPercentage = targetEquityPercentage + targetBondPercentage + targetOtherPercentage;
    const normalizedTargetEquityPercentage = (totalTargetPercentage === 0) ? 0 : (targetEquityPercentage / totalTargetPercentage) * 100;
    const normalizedTargetBondPercentage = (totalTargetPercentage === 0) ? 0 : (targetBondPercentage / totalTargetPercentage) * 100;
    const normalizedTargetOtherPercentage = (totalTargetPercentage === 0) ? 0 : (targetOtherPercentage / totalTargetPercentage) * 100;

    // Calculate target values based on the total current portfolio value
    const targetEquityValue = currentPortfolioValue * (normalizedTargetEquityPercentage / 100);
    const targetBondValue = currentPortfolioValue * (normalizedTargetBondPercentage / 100);
    const targetOtherValue = currentPortfolioValue * (normalizedTargetOtherPercentage / 100);

    // Calculate rebalancing amounts
    const rebalanceAmountEquity = targetEquityValue - currentEquityValue;
    const rebalanceAmountBond = targetBondValue - currentBondValue;
    const rebalanceAmountOther = targetOtherValue - currentOtherValue;

    // Calculate current actual percentages (handle division by zero if currentPortfolioValue is 0, though we already check above)
    const currentEquityPercentageActual = (currentPortfolioValue === 0) ? 0 : (currentEquityValue / currentPortfolioValue) * 100;
    const currentBondPercentageActual = (currentPortfolioValue === 0) ? 0 : (currentBondValue / currentPortfolioValue) * 100;
    const currentOtherPercentageActual = (currentPortfolioValue === 0) ? 0 : (currentOtherValue / currentPortfolioValue) * 100;

    return {
        rebalanceAmountEquity: rebalanceAmountEquity,
        rebalanceAmountBond: rebalanceAmountBond,
        rebalanceAmountOther: rebalanceAmountOther,
        currentEquityPercentageActual: currentEquityPercentageActual,
        currentBondPercentageActual: currentBondPercentageActual,
        currentOtherPercentageActual: currentOtherPercentageActual
    };
}
