const start = parseFloat(inputs.initialInvestment) || 0;
const end = parseFloat(inputs.endingBalance) || 0;
const years = parseFloat(inputs.years) || 0;

if (start === 0) return { totalReturn: 0, annualizedReturn: 0, profit: 0 };

const profit = end - start;
const totalReturn = (profit / start) * 100;

let cagr = 0;
if (end > 0 && start > 0 && years > 0) {
    cagr = (Math.pow(end / start, 1 / years) - 1) * 100;
}

return {
    totalReturn: totalReturn,
    annualizedReturn: cagr,
    profit: profit
};
