function calculate(inputs) {
  const { 
    numDeviceTypes, 
    avgDevicesPerType, 
    deviceComplexity, 
    networkComplexity, 
    dataSensitivity, 
    auditDepth, 
    complianceNeeds, 
    teamAvailability 
  } = inputs;

  // Constants
  const DAILY_RATE = 1500; // Average daily rate for a cybersecurity consultant in USD
  const WORKING_DAYS_PER_WEEK = 5;

  // Base efforts (in person-days)
  // A base effort per device type for initial analysis, setup, and general assessment
  const BASE_EFFORT_PER_DEVICE_TYPE = 4; // 4 person-days per unique device type
  // Base effort for network infrastructure assessment specific to IoT architecture
  const BASE_NETWORK_EFFORT = 10; // 10 person-days for network foundational assessment
  // Base effort for reporting, documentation, and compliance mapping
  const BASE_REPORTING_EFFORT = 8; // 8 person-days for standard reporting

  // Input sanitization and min values
  const actualNumDeviceTypes = Math.max(1, numDeviceTypes || 1);
  const actualAvgDevicesPerType = Math.max(1, avgDevicesPerType || 1);

  // Multipliers for complexity, scope, and other factors
  const complexityMultipliers = {
    device: {
      'low': 0.8,
      'medium': 1.0,
      'high': 1.5
    },
    network: {
      'simple': 0.7,
      'moderate': 1.0,
      'complex': 1.3
    },
    data: {
      'non-sensitive': 0.9,
      'pii-confidential': 1.1,
      'critical-regulated': 1.4
    },
    audit: {
      'va': 0.8,
      'pt': 1.2,
      'full-scope': 1.6
    },
    compliance: {
      'none': 0.8,
      'standard': 1.1,
      'high': 1.4
    },
    team: {
      'none': 1.2, // More external effort needed
      'partial': 1.0,
      'significant': 0.8 // Less external effort needed
    }
  };

  // Calculate total device analysis effort
  // The average devices per type is a scale factor impacting the depth per type.
  // For simplicity, let's say higher 'avgDevicesPerType' indicates a need for more robust, scalable testing procedures per type.
  const deviceScaleFactor = 1 + Math.log10(actualAvgDevicesPerType) / 2;
  let deviceAnalysisEffort = (BASE_EFFORT_PER_DEVICE_TYPE * actualNumDeviceTypes * complexityMultipliers.device[deviceComplexity]) * deviceScaleFactor;

  // Calculate network assessment effort
  let networkAssessmentEffort = BASE_NETWORK_EFFORT * complexityMultipliers.network[networkComplexity];

  // Calculate reporting & compliance effort
  let reportingComplianceEffort = BASE_REPORTING_EFFORT * complexityMultipliers.compliance[complianceNeeds];

  // Apply overall audit depth and data sensitivity multipliers to total initial effort
  let totalBaseEffort = deviceAnalysisEffort + networkAssessmentEffort + reportingComplianceEffort;
  totalBaseEffort *= complexityMultipliers.audit[auditDepth];
  totalBaseEffort *= complexityMultipliers.data[dataSensitivity];

  // Adjust effort based on internal team availability
  let totalAdjustedEffortDays = totalBaseEffort * complexityMultipliers.team[teamAvailability];

  // Apply minimum and maximum caps for realism
  const MIN_EFFORT_DAYS = 15; // Minimum effort even for small scopes
  const MAX_EFFORT_DAYS = 500; // Maximum realistic effort for a single project
  totalAdjustedEffortDays = Math.max(MIN_EFFORT_DAYS, Math.min(MAX_EFFORT_DAYS, totalAdjustedEffortDays));

  // Calculate outputs
  const estimatedEffortWeeks = totalAdjustedEffortDays / WORKING_DAYS_PER_WEEK;
  const estimatedCost = totalAdjustedEffortDays * DAILY_RATE;

  // Calculate Risk Reduction Score (qualitative)
  let riskReductionScore = 0;
  switch (auditDepth) {
    case 'va': riskReductionScore += 40; break;
    case 'pt': riskReductionScore += 65; break;
    case 'full-scope': riskReductionScore += 90; break;
  }
  switch (dataSensitivity) {
    case 'non-sensitive': riskReductionScore += 5; break;
    case 'pii-confidential': riskReductionScore += 10; break;
    case 'critical-regulated': riskReductionScore += 15; break;
  }
  switch (complianceNeeds) {
    case 'none': riskReductionScore += 5; break;
    case 'standard': riskReductionScore += 10; break;
    case 'high': riskReductionScore += 15; break;
  }
  // Max score could be 90 + 15 + 15 = 120, normalize to 100
  riskReductionScore = Math.min(100, Math.round(riskReductionScore * (100/120)));

  return {
    estimatedCost: estimatedCost,
    estimatedEffortWeeks: estimatedEffortWeeks,
    riskReductionScore: riskReductionScore
  };
}
