function calculateROI(inputs) {
    const smartThermostatTotalCost = inputs.smartThermostatTotalCost || 0;
    const leakDetectorTotalCost = inputs.leakDetectorTotalCost || 0;
    const pipeHeaterTotalCost = inputs.pipeHeaterTotalCost || 0;
    const installationCost = inputs.installationCost || 0;
    const averageMonthlyEnergyBill = inputs.averageMonthlyEnergyBill || 0;
    const energySavingsPercentage = inputs.energySavingsPercentage || 0;
    const damageProbability = inputs.damageProbability || 0;
    const averageRepairCost = inputs.averageRepairCost || 0;
    const insuranceDeductibleAmount = inputs.insuranceDeductibleAmount || 0;
    const expectedDeviceLifespan = inputs.expectedDeviceLifespan || 1; // Min 1 year lifespan

    // 1. Calculate Total Initial Investment
    const totalInitialInvestment = smartThermostatTotalCost + leakDetectorTotalCost + pipeHeaterTotalCost + installationCost;

    // 2. Calculate Estimated Annual Energy Savings
    const annualEnergySavings = (averageMonthlyEnergyBill * 12) * (energySavingsPercentage / 100);

    // 3. Calculate Estimated Annual Damage Prevention Savings
    let potentialDamageAvoidedPerEvent = Math.max(0, averageRepairCost - insuranceDeductibleAmount);
    const annualDamagePreventionSavings = potentialDamageAvoidedPerEvent * (damageProbability / 100);

    // 4. Calculate Total Estimated Annual Savings
    const totalAnnualSavings = annualEnergySavings + annualDamagePreventionSavings;

    // 5. Calculate Estimated Payback Period
    let paybackPeriodYears = Infinity;
    if (totalAnnualSavings > 0) {
        paybackPeriodYears = totalInitialInvestment / totalAnnualSavings;
        if (paybackPeriodYears < 0) paybackPeriodYears = Infinity; // Should not happen with positive savings
    } else if (totalInitialInvestment === 0) {
        paybackPeriodYears = 0;
    }

    // 6. Calculate Return on Investment (ROI)
    let roiPercentage = -100; // Default to 100% loss if no investment or savings
    if (totalInitialInvestment > 0) {
        roiPercentage = (((totalAnnualSavings * expectedDeviceLifespan) - totalInitialInvestment) / totalInitialInvestment) * 100;
    } else if (totalInitialInvestment === 0 && totalAnnualSavings > 0) {
        roiPercentage = Infinity; // Infinite ROI if no investment but still savings
    } else if (totalInitialInvestment === 0 && totalAnnualSavings === 0) {
        roiPercentage = 0; // No investment, no savings, effectively 0 ROI
    }

    return {
        totalInitialInvestment: totalInitialInvestment,
        annualEnergySavings: annualEnergySavings,
        annualDamagePreventionSavings: annualDamagePreventionSavings,
        totalAnnualSavings: totalAnnualSavings,
        paybackPeriodYears: isFinite(paybackPeriodYears) ? paybackPeriodYears : 'N/A',
        roiPercentage: isFinite(roiPercentage) ? roiPercentage : 'N/A'
    };
}
