function calculate({ economic_area_workforce_size, current_avg_monthly_wage, current_avg_employer_social_contribution_rate, current_avg_employer_severance_cost_rate, proposed_wage_change_percent, proposed_employer_social_contribution_change_percent_points, proposed_employer_severance_cost_change_percent_points, labor_demand_elasticity_wage, marginal_propensity_to_consume, avg_business_revenue_per_worker_annual }) {

  // Input validation and default values
  economic_area_workforce_size = Math.max(0, economic_area_workforce_size || 0);
  current_avg_monthly_wage = Math.max(0, current_avg_monthly_wage || 0);
  current_avg_employer_social_contribution_rate = Math.max(0, current_avg_employer_social_contribution_rate || 0);
  current_avg_employer_severance_cost_rate = Math.max(0, current_avg_employer_severance_cost_rate || 0);
  labor_demand_elasticity_wage = labor_demand_elasticity_wage || 0; // Can be positive or negative, but typically negative
  marginal_propensity_to_consume = Math.min(Math.max(0, marginal_propensity_to_consume || 0), 1); // Clamp between 0 and 1
  avg_business_revenue_per_worker_annual = Math.max(0, avg_business_revenue_per_worker_annual || 0);

  // Handle edge cases where core inputs are zero to prevent division by zero or nonsensical results
  if (economic_area_workforce_size === 0 || current_avg_monthly_wage === 0) {
    return {
      projected_employment_change_abs: 0,
      projected_avg_monthly_wage_abs_change: 0,
      projected_business_labor_cost_impact_percent_of_total_revenue: 0,
      projected_consumer_purchasing_power_change_annual: 0,
      estimated_overall_economic_activity_impact_annual: 0
    };
  }

  // Step 1: Calculate new average wage and its absolute change
  const wage_change_factor = 1 + proposed_wage_change_percent / 100;
  const new_avg_monthly_wage = current_avg_monthly_wage * wage_change_factor;
  const projected_avg_monthly_wage_abs_change = new_avg_monthly_wage - current_avg_monthly_wage;

  // Step 2: Calculate employment change based on labor demand elasticity
  // A 1% wage change leads to (elasticity * 1%) change in employment.
  const employment_change_percent = labor_demand_elasticity_wage * proposed_wage_change_percent;
  let projected_employment_change_abs = economic_area_workforce_size * employment_change_percent / 100;

  // Ensure workforce does not drop below zero
  const new_total_workforce_raw = economic_area_workforce_size + projected_employment_change_abs;
  const new_total_workforce = Math.max(0, new_total_workforce_raw);
  projected_employment_change_abs = new_total_workforce - economic_area_workforce_size; // Recalculate based on floor

  // Step 3: Calculate new employer contribution rates
  const new_employer_social_contribution_rate = Math.max(0, current_avg_employer_social_contribution_rate + proposed_employer_social_contribution_change_percent_points);
  const new_employer_severance_cost_rate = Math.max(0, current_avg_employer_severance_cost_rate + proposed_employer_severance_cost_change_percent_points);

  // Step 4: Calculate total labor costs for businesses (monthly per worker, then annual for the area)
  const current_total_labor_cost_per_worker_monthly = current_avg_monthly_wage * (1 + (current_avg_employer_social_contribution_rate / 100) + (current_avg_employer_severance_cost_rate / 100));
  const projected_total_labor_cost_per_worker_monthly = new_avg_monthly_wage * (1 + (new_employer_social_contribution_rate / 100) + (new_employer_severance_cost_rate / 100));

  const current_total_labor_bill_annual = current_total_labor_cost_per_worker_monthly * economic_area_workforce_size * 12;
  const projected_total_labor_bill_annual = projected_total_labor_cost_per_worker_monthly * new_total_workforce * 12;

  const labor_cost_impact_annual = projected_total_labor_bill_annual - current_total_labor_bill_annual;

  // Step 5: Calculate business labor cost impact as a percentage of total revenue
  let projected_business_labor_cost_impact_percent_of_total_revenue = 0;
  const total_area_revenue_annual = economic_area_workforce_size * avg_business_revenue_per_worker_annual; // Use initial workforce for current revenue baseline
  if (total_area_revenue_annual > 0) {
    projected_business_labor_cost_impact_percent_of_total_revenue = (labor_cost_impact_annual / total_area_revenue_annual) * 100;
  }

  // Step 6: Calculate consumer purchasing power change
  // This is the total change in gross monthly wages for the (new) workforce, annualized.
  const projected_consumer_purchasing_power_change_annual = projected_avg_monthly_wage_abs_change * new_total_workforce * 12;

  // Step 7: Estimate overall economic activity impact using the multiplier effect
  let estimated_overall_economic_activity_impact_annual = 0;
  // The multiplier is 1 / (1 - MPC). If MPC is 1, multiplier is infinite; if MPC is 0, multiplier is 1.
  // If MPC is extremely close to 1, multiplier can be very large. Clamp for practical use.
  const effective_mpc = Math.min(marginal_propensity_to_consume, 0.9999); // Prevent division by zero if MPC is 1
  const spending_multiplier = 1 / (1 - effective_mpc);

  // Only apply multiplier if purchasing power has a positive change, as negative multiplier effect often differs
  if (projected_consumer_purchasing_power_change_annual > 0) {
    estimated_overall_economic_activity_impact_annual = projected_consumer_purchasing_power_change_annual * spending_multiplier;
  } else {
    // For negative changes, a simple direct impact might be more appropriate than a full multiplier, or a different multiplier.
    // For simplicity here, we'll use a dampened multiplier for negative impacts or just the direct impact if multiplier < 1.
    // A more complex model might have different multipliers for positive vs. negative shocks.
    // Here, we'll just use the direct change if the spending_multiplier is less than 1, otherwise the calculated multiplier.
    // To simplify: Apply multiplier to both positive and negative changes.
    estimated_overall_economic_activity_impact_annual = projected_consumer_purchasing_power_change_annual * spending_multiplier;
  }

  return {
    projected_employment_change_abs: projected_employment_change_abs,
    projected_avg_monthly_wage_abs_change: projected_avg_monthly_wage_abs_change,
    projected_business_labor_cost_impact_percent_of_total_revenue: projected_business_labor_cost_impact_percent_of_total_revenue,
    projected_consumer_purchasing_power_change_annual: projected_consumer_purchasing_power_change_annual,
    estimated_overall_economic_activity_impact_annual: estimated_overall_economic_activity_impact_annual
  };
}
